import React, { useEffect, useState } from "react";
import config from "../utils/config";
import "../styles/styles.css";

const AdminPanel = () => {
    const [movies, setMovies] = useState([]);
    const [series, setSeries] = useState([]);
    const [currentMoviePage, setCurrentMoviePage] = useState(1);
    const [currentSeriesPage, setCurrentSeriesPage] = useState(1);
    const [itemsPerPage, setItemsPerPage] = useState(10);
    const [searchQuery, setSearchQuery] = useState("");
    const [selectedItem, setSelectedItem] = useState(null);
    const [editData, setEditData] = useState({});
    const [error, setError] = useState("");
    const [isAdding, setIsAdding] = useState(false);
    const [newItem, setNewItem] = useState({ title: "", genre: "", releaseYear: "", duration: "", language: "", type: "movies" });

    useEffect(() => {
        const fetchData = async () => {
            const accessToken = sessionStorage.getItem("accessToken");

            if (!accessToken) {
                setError("‚ùå No hay token de autenticaci√≥n. Inicia sesi√≥n nuevamente.");
                return;
            }

            try {
                const moviesResponse = await fetch(`${config.API_URL}/api/admin/movies`, {
                    method: "GET",
                    headers: { "Authorization": `Bearer ${accessToken}`, "Content-Type": "application/json" }
                });

                const seriesResponse = await fetch(`${config.API_URL}/api/admin/series`, {
                    method: "GET",
                    headers: { "Authorization": `Bearer ${accessToken}`, "Content-Type": "application/json" }
                });

                if (!moviesResponse.ok || !seriesResponse.ok) {
                    throw new Error("Error al obtener datos.");
                }

                setMovies(await moviesResponse.json());
                setSeries(await seriesResponse.json());
            } catch (error) {
                setError(error.message);
            }
        };

        fetchData();
    }, []);

    // üîπ Correcci√≥n: Definir correctamente la funci√≥n handleSearchChange
    const handleSearchChange = (e) => {
        setSearchQuery(e.target.value.toLowerCase());
    };

    const handleCreate = async () => {
        console.log("üìå handleCreate activado con:", newItem); // Registro de solicitud
        const accessToken = sessionStorage.getItem("accessToken");

        if (!accessToken) {
            setError("‚ùå No hay token de autenticaci√≥n.");
            return;
        }

        try {
            const response = await fetch(`${config.API_URL}/api/admin/${newItem.type}`, {
                method: "POST",
                headers: { 
                    "Authorization": `Bearer ${accessToken}`,
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(newItem)
            });

            console.log("üìå Solicitud enviada al backend:", JSON.stringify(newItem));

            if (!response.ok) {
                throw new Error("Error al crear contenido.");
            }

            const createdItem = await response.json();

            if (newItem.type === "movies") {
                setMovies([...movies, createdItem]);
            } else {
                setSeries([...series, createdItem]);
            }

            setIsAdding(false);
        } catch (error) {
            console.error("‚ùå Error al crear contenido:", error);
            setError(error.message);
        }
    };

    const handleInputChange = (e) => {
        setNewItem({ ...newItem, [e.target.name]: e.target.value });
    };

    return (
        <div className="admin-container">
            <h1>Panel de Administraci√≥n</h1>
            {error && <p className="error-message">{error}</p>}

            <input 
                type="text" 
                placeholder="Buscar t√≠tulo..." 
                onChange={handleSearchChange} 
                className="search-box"
            />

            {/* Tabla de Pel√≠culas */}
            <h2>Pel√≠culas</h2>
            <button className="add-btn" onClick={() => { 
                console.log("üìå Bot√≥n A√±adir presionado"); 
                setIsAdding(true); 
                setNewItem({ title: "", genre: "", releaseYear: "", duration: "", language: "", type: "movies" }); 
            }}>
                A√±adir Pel√≠cula
            </button>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>T√≠tulo</th>
                        <th>G√©nero</th>
                        <th>A√±o</th>
                        <th>Duraci√≥n</th>
                        <th>Idioma</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {movies.filter(movie => movie.Title.toLowerCase().includes(searchQuery)).map(movie => (
                        <tr key={movie.Id_Movie}>
                            <td>{movie.Id_Movie}</td>
                            <td>{movie.Title}</td>
                            <td>{movie.Genre}</td>
                            <td>{movie.Release_Year}</td>
                            <td>{movie.Duration} min</td>
                            <td>{movie.Language}</td>
                            <td>
                                <button className="edit-btn">Modificar</button>
                                <button className="delete-btn">Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h2>Series</h2>
<button className="add-btn" onClick={() => { 
    console.log("üìå Bot√≥n A√±adir Serie presionado"); 
    setIsAdding(true); 
    setNewItem({ title: "", genre: "", releaseYear: "", seasons: "", language: "", type: "series" }); 
}}>
    A√±adir Serie
</button>
<table className="users-table">
    <thead>
        <tr>
            <th>ID</th>
            <th>T√≠tulo</th>
            <th>G√©nero</th>
            <th>A√±o</th>
            <th>Temporadas</th>
            <th>Idioma</th>
            <th>Acciones</th>
        </tr>
    </thead>
    <tbody>
        {series.filter(serie => serie.Title.toLowerCase().includes(searchQuery)).map(serie => (
            <tr key={serie.Id_Series}>
                <td>{serie.Id_Series}</td>
                <td>{serie.Title}</td>
                <td>{serie.Genre}</td>
                <td>{serie.Release_Year}</td>
                <td>{serie.Seasons}</td>
                <td>{serie.Language}</td>
                <td>
                    <button className="edit-btn">Modificar</button>
                    <button className="delete-btn">Eliminar</button>
                </td>
            </tr>
        ))}
    </tbody>
</table>

            {/* Modal para a√±adir contenido */}
            {isAdding && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>A√±adir {newItem.type === "movies" ? "Pel√≠cula" : "Serie"}</h2>
                        <label>T√≠tulo:</label>
                        <input type="text" name="title" value={newItem.title} onChange={handleInputChange} />

                        <label>G√©nero:</label>
                        <input type="text" name="genre" value={newItem.genre} onChange={handleInputChange} />

                        <label>A√±o de estreno:</label>
                        <input type="number" name="releaseYear" value={newItem.releaseYear} onChange={handleInputChange} />

                        <label>Duraci√≥n (minutos):</label>
                        <input type="number" name="duration" value={newItem.duration} onChange={handleInputChange} />

                        <label>Idioma:</label>
                        <input type="text" name="language" value={newItem.language} onChange={handleInputChange} />

                        <button className="save-btn" onClick={handleCreate}>Guardar</button>
                        <button className="cancel-btn" onClick={() => setIsAdding(false)}>Cancelar</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminPanel;
