import React, { useState, useEffect } from "react";
import config from "../utils/config";
import "../styles/styles.css";
import Modal from "../components/Modal";
import 'font-awesome/css/font-awesome.min.css';

const Index = () => {
  const [moviesList, setMoviesList] = useState([]);
  const [seriesList, setSeriesList] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Fetch películas y series
  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/movies`);
        const data = await response.json();
        setMoviesList(data);
      } catch (error) {
        console.error("❌ Error al cargar películas:", error);
      }
    };

    const fetchSeries = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/series`);
        const data = await response.json();
        setSeriesList(data);
      } catch (error) {
        console.error("❌ Error al cargar series:", error);
      }
    };

    fetchMovies();
    fetchSeries();
  }, []);

  // Función para mostrar el modal con detalles (se utiliza al hacer clic en la tarjeta)
  const handleClick = async (id, type) => {
    try {
      const response = await fetch(`${config.API_URL}/api/${type}/${id}`);
      const data = await response.json();
      setSelectedItem({ ...data, type });
      setIsModalOpen(true);
    } catch (error) {
      console.error(`❌ Error al cargar ${type}:`, error);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedItem(null);
  };

  // Componente interno para cada tarjeta de contenido
 // Dentro de Index.jsx

// Subcomponente para cada tarjeta, con funcionalidad de favorito
const ContentCard = ({ item, type }) => {
  const [isFavorite, setIsFavorite] = useState(false);
  const [message, setMessage] = useState("");

  // En el useEffect se determina el estado inicial leyendo el objeto user en sessionStorage
  useEffect(() => {
    const userStr = sessionStorage.getItem("user");
    if (userStr) {
      const user = JSON.parse(userStr);
      // Se asume que el identificador puede venir en diferentes campos
      const itemId = item.id || item.Id_Movie || item.Id_Series;
      if (user.Favorites) {
        const favs = user.Favorites.split(",").map(fav => fav.trim());
        setIsFavorite(favs.includes(String(itemId)));
      }
    }
  }, [item]);

  const handleFavoriteClick = async (e) => {
    e.stopPropagation(); // Evita activar el clic principal de la tarjeta
    const itemId = item.id || item.Id_Movie || item.Id_Series;
    const newFavState = !isFavorite;
    setIsFavorite(newFavState);
    setMessage(newFavState ? "Añadido a favoritos" : "Eliminado de favoritos");
    setTimeout(() => setMessage(""), 2000);

    // Actualizar el objeto user en sessionStorage para persistir el estado del favorito
    const userStr = sessionStorage.getItem("user");
    if (userStr) {
      const user = JSON.parse(userStr);
      let favs = user.Favorites
        ? user.Favorites.split(",").map(fav => fav.trim()).filter(fav => fav !== "")
        : [];
      if (newFavState) {
        if (!favs.includes(String(itemId))) {
          favs.push(String(itemId));
        }
      } else {
        favs = favs.filter(fav => fav !== String(itemId));
      }
      user.Favorites = favs.join(",");
      sessionStorage.setItem("user", JSON.stringify(user));
    }

    // Realizar la actualización en la base de datos mediante PUT
    try {
      const userObj = JSON.parse(sessionStorage.getItem("user"));
      const response = await fetch(`${config.API_URL}/api/usuario/favorites`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          userId: userObj.id,
          itemId: itemId,
          isFavorite: newFavState,
        }),
      });
      if (!response.ok) {
        throw new Error("Error actualizando favoritos");
      }
      const data = await response.json();
      // Opcional: actualizar algo en el estado o notificar al usuario
    } catch (error) {
      console.error("❌ Error actualizando favoritos:", error);
    }
  };

  return (
    <div
      className="content-card"
      onClick={() => handleClick(item.id || item.Id_Movie || item.Id_Series, type)}
    >
      <div className="card-image-container" style={{ position: "relative" }}>
        <img src={`${config.API_URL}${item.Image}`} alt={item.Title} />
        <div
          className="favorite-icon"
          onClick={handleFavoriteClick}
          style={{
            position: "absolute",
            top: "10px",
            left: "10px",
            cursor: "pointer",
          }}
        >
          <i
            className={`fa ${isFavorite ? "fa-star" : "fa-star-o"}`}
            style={{ fontSize: "24px", color: "gold" }}
          />
          {message && (
            <div
              className="favorite-message"
              style={{
                position: "absolute",
                top: "0",
                left: "40px",
                background: "rgba(0,0,0,0.7)",
                padding: "3px 8px",
                borderRadius: "5px",
                color: "white",
                fontSize: "14px",
              }}
            >
              {message}
            </div>
          )}
        </div>
      </div>
      <p>{item.Title}</p>
    </div>
  );
};


  return (
    <div className="index-container">
      <h1>Bienvenido a Blockbuster</h1>
      <p>Explora nuestras series y películas.</p>

      <div className="content-section">
        <h2>Series Populares</h2>
        <div className="content-carousel">
          {seriesList.map((series) => (
            <ContentCard key={series.Id_Series} item={series} type="series" />
          ))}
        </div>
      </div>

      <div className="content-section">
        <h2>Películas Destacadas</h2>
        <div className="content-carousel">
          {moviesList.map((movie) => (
            <ContentCard key={movie.Id_Movie} item={movie} type="movies" />
          ))}
        </div>
      </div>

      {/* Modal para mostrar detalles de películas o series */}
      <Modal isOpen={isModalOpen} onClose={closeModal} data={selectedItem} />
    </div>
  );
};

export default Index;
