    setIsFavorite(newFavState);

    console.log("üìå Antes de modificar favoritos:", sessionStorage.getItem("user"));

    // Establecer mensaje flotante global y local
    setFavoriteMessage(newFavState ? "A√±adido a favoritos" : "Eliminado de favoritos");
    setTimeout(() => setFavoriteMessage(""), 2000);
    setMessage(newFavState ? "A√±adido a favoritos" : "Eliminado de favoritos");
    setTimeout(() => setMessage(""), 2000);

    // Actualizar sessionStorage (limpiando el campo Favorites si contiene 'undefined')
    const userStr = sessionStorage.getItem("user");
    if (userStr) {
      const user = JSON.parse(userStr);
      // Si Favorites contiene "undefined", se reinicia a cadena vac√≠a.
      if (!user.Favorites || user.Favorites === "undefined") {
        user.Favorites = "";
      }
      let favs = user.Favorites
