// Portada.jsx
import React from "react";
import { useNavigate } from "react-router-dom";
import "../styles/portada.css"; // Para los demás estilos del contenido

const Portada = () => {
  const navigate = useNavigate();

  // Aquí usamos process.env.PUBLIC_URL para apuntar a la imagen que está en public/uploads
  const backgroundStyle = {
    background: `url(${process.env.PUBLIC_URL}/uploads/background.jpg) no-repeat center center/cover`,
    height: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  };

  return (
    <div style={backgroundStyle}>
      <div className="portada-content">
        <h1>Bienvenido a BlockBuster</h1>
        <p>
          Descubre, explora y disfruta de tus series y películas favoritas.
        </p>
        <div className="portada-buttons">
          <button onClick={() => navigate("/login")}>Iniciar Sesión</button>
          <button onClick={() => navigate("/register")}>Registrarse</button>
        </div>
      </div>
    </div>
  );
};

export default Portada;
