import React, { useEffect, useState } from "react";
import config from "../utils/config";

const UserProfile = () => {
  const [userRatings, setUserRatings] = useState([]);
  const user = JSON.parse(sessionStorage.getItem("user"));

  useEffect(() => {
    const fetchUserRatings = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/usuario/${user.Id_User}/ratings`);
        if (response.ok) {
          const data = await response.json();
          setUserRatings(data);
        } else {
          console.error("❌ Error fetching user ratings, status:", response.status);
        }
      } catch (error) {
        console.error("❌ Error fetching user ratings:", error);
      }
    };

    fetchUserRatings();
  }, [user.Id_User]);

  return (
    <div className="user-profile">
      <h1>Perfil de {user.Name}</h1>
      <h2>Tus puntuaciones</h2>
      {userRatings.length === 0 ? (
        <p>No has realizado ninguna valoración</p>
      ) : (
        <ul className="ratings-list">
          {userRatings.map((rating) => (
            <li key={rating.id}>
              <span>
                Contenido: {rating.item_key} - Puntuación: {rating.rating}
              </span>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default UserProfile;
