import React, { useEffect, useState } from "react";
import config from "../utils/config";
import "../styles/styles.css";

const AdminPanel = () => {
    const [movies, setMovies] = useState([]);
    const [series, setSeries] = useState([]);
    const [genres, setGenres] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [error, setError] = useState("");
    const [isAdding, setIsAdding] = useState(false);
    const [isEditing, setIsEditing] = useState(false);
    const [editItem, setEditItem] = useState(null);
    const [newItem, setNewItem] = useState({ title: "", genreId: "", releaseYear: "", duration: "", language: "", type: "movies" });

    useEffect(() => {
        const fetchData = async () => {
            const accessToken = sessionStorage.getItem("accessToken");

            if (!accessToken) {
                setError("❌ No hay token de autenticación.");
                return;
            }

            try {
                const [moviesResponse, seriesResponse, genresResponse] = await Promise.all([
                    fetch(`${config.API_URL}/api/admin/movies`, {
                        method: "GET",
                        headers: { "Authorization": `Bearer ${accessToken}`, "Content-Type": "application/json" }
                    }),
                    fetch(`${config.API_URL}/api/admin/series`, {
                        method: "GET",
                        headers: { "Authorization": `Bearer ${accessToken}`, "Content-Type": "application/json" }
                    }),
                    fetch(`${config.API_URL}/api/admin/genres`, {
                        method: "GET",
                        headers: { "Authorization": `Bearer ${accessToken}`, "Content-Type": "application/json" }
                    })
                ]);

                if (!moviesResponse.ok || !seriesResponse.ok || !genresResponse.ok) {
                    throw new Error("Error al obtener datos.");
                }

                setMovies(await moviesResponse.json());
                setSeries(await seriesResponse.json());
                setGenres(await genresResponse.json());
            } catch (error) {
                setError(error.message);
            }
        };

        fetchData();
    }, []);

    const handleCreate = async () => {
        const accessToken = sessionStorage.getItem("accessToken");

        if (!accessToken) {
            setError("❌ No hay token de autenticación.");
            return;
        }

        try {
            const response = await fetch(`${config.API_URL}/api/admin/${newItem.type}`, {
                method: "POST",
                headers: { "Authorization": `Bearer ${accessToken}`, "Content-Type": "application/json" },
                body: JSON.stringify(newItem)
            });

            if (!response.ok) {
                throw new Error("Error al crear contenido.");
            }

            const createdItem = await response.json();
            newItem.type === "movies" ? setMovies([...movies, createdItem]) : setSeries([...series, createdItem]);

            setIsAdding(false);
        } catch (error) {
            setError(error.message);
        }
    };

    const handleDelete = async (type, id) => {
        const accessToken = sessionStorage.getItem("accessToken");

        try {
            const response = await fetch(`${config.API_URL}/api/admin/${type}/${id}`, {
                method: "DELETE",
                headers: { "Authorization": `Bearer ${accessToken}` }
            });

            if (!response.ok) {
                throw new Error("Error al eliminar contenido.");
            }

            type === "movies" ? setMovies(movies.filter(movie => movie.id !== id)) : setSeries(series.filter(serie => serie.id !== id));
        } catch (error) {
            setError(error.message);
        }
    };

    const handleEdit = (item) => {
        setEditItem(item);
        setIsEditing(true);
    };

    const handleUpdate = async () => {
        const accessToken = sessionStorage.getItem("accessToken");

        try {
            const response = await fetch(`${config.API_URL}/api/admin/${editItem.type}/${editItem.id}`, {
                method: "PUT",
                headers: { "Authorization": `Bearer ${accessToken}`, "Content-Type": "application/json" },
                body: JSON.stringify(editItem)
            });

            if (!response.ok) {
                throw new Error("Error al actualizar contenido.");
            }

            setIsEditing(false);
        } catch (error) {
            setError(error.message);
        }
    };

    return (
        <div className="admin-container">
            <h1>Panel de Administración</h1>
            {error && <p className="error-message">{error}</p>}

            <input type="text" placeholder="Buscar título..." onChange={(e) => setSearchQuery(e.target.value.toLowerCase())} className="search-box" />

            <h2>Películas</h2>
            <button className="add-btn" onClick={() => setIsAdding(true)}>Añadir Película</button>
            <table>
                <tbody>
                    {movies.map(movie => (
                        <tr key={movie.id}>
                            <td>{movie.Title}</td>
                            <td>
                                <button onClick={() => handleEdit(movie)}>Modificar</button>
                                <button onClick={() => handleDelete("movies", movie.id)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h2>Series</h2>
            <button className="add-btn" onClick={() => setIsAdding(true)}>Añadir Serie</button>
            <table>
                <tbody>
                    {series.map(serie => (
                        <tr key={serie.id}>
                            <td>{serie.Title}</td>
                            <td>
                                <button onClick={() => handleEdit(serie)}>Modificar</button>
                                <button onClick={() => handleDelete("series", serie.id)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {isEditing && (
                <div className="modal">
                    <input type="text" value={editItem.Title} onChange={(e) => setEditItem({ ...editItem, Title: e.target.value })} />
                    <button onClick={handleUpdate}>Actualizar</button>
                    <button onClick={() => setIsEditing(false)}>Cancelar</button>
                </div>
            )}
        </div>
    );
};

export default AdminPanel;
