import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import config from "../utils/config";
import "../styles/styles.css";

const Register = () => {
  const [user, setUser] = useState({
    name: "",
    email: "",
    address: "",
    password: "",
    confirmPassword: "",
    avatar: "", 
    subscription: "", 
  });

  const [selectedAvatar, setSelectedAvatar] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  // 游댳 Lista de avatares con im치genes almacenadas en la carpeta p칰blica del backend
  const avatarList = [
    { id: 0, url: `${config.API_URL}/uploads/avatars/7309681.jpg`, description: "Avatar1" },
    { id: 1, url: `${config.API_URL}/uploads/avatars/9334176.jpg`, description: "Avatar2" },
    { id: 2, url: `${config.API_URL}/uploads/avatars/10491830.jpg`, description: "Avatar3" }
  ];

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleAvatarSelect = (avatarUrl) => {
    setSelectedAvatar(avatarUrl);
    setUser({ ...user, avatar: avatarUrl }); // 游댳 Guardamos la URL en el estado 'user'
  };

  const validatePassword = (password) => {
    const regex = /^(?=.*[A-Z])(?=.*[a-z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    return regex.test(password);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage("");

    if (user.password !== user.confirmPassword) {
      setErrorMessage("Las contrase침as no coinciden.");
      return;
    }

    if (!user.subscription) {
      setErrorMessage("Debes seleccionar un tipo de suscripci칩n.");
      return;
    }
    if (!validatePassword(user.password)) {
      setErrorMessage("La contrase침a debe tener al menos 1 may칰scula, 1 min칰scula, 1 n칰mero y 1 car치cter especial.");
      return;
    }

    if (!user.avatar) {
      setErrorMessage("Debes seleccionar un avatar.");
      return;
    }

    try {
      const response = await fetch(`${config.API_URL}/register`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(user),
      });

      if (!response.ok) {
        const { message } = await response.json();
        throw new Error(message || "Error al registrar. Int칠ntalo de nuevo.");
      }

      alert("Registro exitoso.");
      navigate("/login", { replace: true });
    } catch (error) {
      setErrorMessage(error.message);
    }
  };

  return (
    <div className="form-container">
      <div className="form-box">
        <h2>Registro</h2>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <form onSubmit={handleSubmit}>
          <input type="text" name="name" placeholder="Nombre" onChange={handleChange} required />
          <input type="email" name="email" placeholder="Correo" onChange={handleChange} required />
          <input type="text" name="address" placeholder="Direcci칩n" onChange={handleChange} required />
          <div className="password-container">
            <input type={showPassword ? "text" : "password"} name="password" placeholder="Contrase침a" onChange={handleChange} required />
          </div>
          <div className="password-container">
            <input type="password" name="confirmPassword" placeholder="Repetir Contrase침a" onChange={handleChange} required />
          </div>
          <button type="button" className="toggle-password" onClick={() => setShowPassword(!showPassword)}>
            {showPassword ? "Ocultar" : "Mostrar"}
          </button>

          {/* 游댳 Secci칩n de selecci칩n de avatar */}
          <h3>Selecciona tu avatar:</h3>
          <div className="avatar-selection">
            {avatarList.map((avatar, index) => (
              <img
                key={index}
                src={avatar.url}
                alt={avatar.description}
                className={`avatar ${selectedAvatar === avatar.url ? "selected" : ""}`}
                onClick={() => handleAvatarSelect(avatar.url)}
              />
            ))}
          </div>

          <button type="submit">Registrar</button>
        </form>
        <p>쯏a tienes cuenta? <a href="/login">Inicia sesi칩n aqu칤</a></p>
      </div>
    </div>
  );
};

export default Register;
