import React, { useEffect, useState } from "react";
import config from "../utils/config";
import "../styles/styles.css";

const AdminUsers = () => {
    const [users, setUsers] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [usersPerPage, setUsersPerPage] = useState(10);
    const [searchQuery, setSearchQuery] = useState("");
    const [error, setError] = useState("");
    const [selectedUser, setSelectedUser] = useState(null); // 🔹 Usuario en edición
    const [editData, setEditData] = useState({ name: "", email: "", role: "" }); // 🔹 Datos a modificar

    useEffect(() => {
        const fetchUsers = async () => {
            const accessToken = sessionStorage.getItem("accessToken");

            if (!accessToken) {
                console.error("❌ No hay token en sessionStorage. ¿El usuario está autenticado?");
                setError("❌ No hay token de autenticación. Inicia sesión nuevamente.");
                return;
            }

            try {
                const response = await fetch(`${config.API_URL}/api/admin/users`, {
                    method: "GET",
                    headers: { 
                        "Authorization": `Bearer ${accessToken}`,
                        "Content-Type": "application/json"
                    }
                });

                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.message || "Error al obtener usuarios.");
                }

                const data = await response.json();
                setUsers(data);
            } catch (error) {
                console.error("❌ Error en fetchUsers:", error);
                setError(error.message);
            }
        };

        fetchUsers();
    }, []);

    const handleEditClick = (user) => {
        setSelectedUser(user);
        setEditData({ name: user.name, email: user.email, role: user.role });
    };

    const handleInputChange = (e) => {
        setEditData({ ...editData, [e.target.name]: e.target.value });
    };

    const handleUpdate = async () => {
        const accessToken = sessionStorage.getItem("accessToken");

        if (!accessToken) {
            setError("❌ No hay token de autenticación.");
            return;
        }

        try {
            const response = await fetch(`${config.API_URL}/api/admin/users/${selectedUser.id}`, {
                method: "PUT",
                headers: { 
                    "Authorization": `Bearer ${accessToken}`,
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(editData)
            });

            if (!response.ok) {
                throw new Error("Error al actualizar usuario.");
            }

            setUsers(users.map(user => user.id === selectedUser.id ? { ...user, ...editData } : user));
            setSelectedUser(null); // 🔹 Cierra el formulario
        } catch (error) {
            setError(error.message);
        }
    };

    return (
        <div className="admin-container">
            <h1>Gestionar Usuarios</h1>
            {error && <p className="error-message">{error}</p>}

            {/* Tabla con botón de edición */}
            <table className="users-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nombre</th>
                        <th>Correo</th>
                        <th>Rol</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>{user.role}</td>
                            <td>
                                <button className="edit-btn" onClick={() => handleEditClick(user)}>Modificar</button>
                                <button className="delete-btn" onClick={() => handleDelete(user.id)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Formulario de edición emergente */}
            {selectedUser && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>Editar Usuario</h2>
                        <label>Nombre:</label>
                        <input type="text" name="name" value={editData.name} onChange={handleInputChange} />

                        <label>Correo:</label>
                        <input type="email" name="email" value={editData.email} onChange={handleInputChange} />

                        <label>Rol:</label>
                        <select name="role" value={editData.role} onChange={handleInputChange}>
                            <option value="admin">Admin</option>
                            <option value="user">Usuario</option>
                        </select>

                        <button className="update-btn" onClick={handleUpdate}>Guardar Cambios</button>
                        <button className="cancel-btn" onClick={() => setSelectedUser(null)}>Cancelar</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminUsers;
