import React, { useEffect, useState } from "react";
import config from "../utils/config";
import "../styles/styles.css";

const AdminPanel = () => {
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);
  const [genres, setGenres] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [error, setError] = useState("");
  const [isAdding, setIsAdding] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editItem, setEditItem] = useState(null);

  // Usamos mayúsculas en las propiedades para que coincidan con lo que retorna el backend.
  const [newItem, setNewItem] = useState({
    Title: "",
    genreId: "",
    releaseYear: "",
    duration: "", // Para películas
    seasons: "",  // Para series
    language: "",
    type: "movies", // "movies" o "series"
  });

  useEffect(() => {
    const fetchData = async () => {
        const accessToken = sessionStorage.getItem("accessToken");

        if (!accessToken) {
            setError("❌ No hay token de autenticación.");
            return;
        }

        try {
            const [moviesResponse, seriesResponse, genresResponse] = await Promise.all([
                fetch(`${config.API_URL}/api/admin/movies`, {
                    method: "GET",
                    headers: { 
                        "Authorization": `Bearer ${accessToken}`,
                        "Content-Type": "application/json"
                    }
                }),
                fetch(`${config.API_URL}/api/admin/series`, {
                    method: "GET",
                    headers: { 
                        "Authorization": `Bearer ${accessToken}`,
                        "Content-Type": "application/json"
                    }
                }),
                fetch(`${config.API_URL}/api/admin/genres`, {
                    method: "GET",
                    headers: { 
                        "Authorization": `Bearer ${accessToken}`,
                        "Content-Type": "application/json"
                    }
                })
            ]);

            if (!moviesResponse.ok || !seriesResponse.ok || !genresResponse.ok) {
                throw new Error("Error al obtener datos.");
            }

            const moviesData = await moviesResponse.json();
            const seriesData = await seriesResponse.json();
            const genresData = await genresResponse.json();

            console.log("📌 Películas recibidas:", JSON.stringify(moviesData, null, 2));
            console.log("📌 Series recibidas:", JSON.stringify(seriesData, null, 2));
            console.log("📌 Géneros recibidos:", JSON.stringify(genresData, null, 2));  

            setMovies(moviesData);
            setSeries(seriesData);
            setGenres(genresData);
        } catch (error) {
            setError(error.message);
        }
    };

    fetchData();
}, []);

  const handleCreate = async () => {
    const accessToken = sessionStorage.getItem("accessToken");
    if (!accessToken) {
      setError("❌ No hay token de autenticación.");
      return;
    }
    try {
      const response = await fetch(`${config.API_URL}/api/admin/${newItem.type}`, {
        method: "POST",
        headers: {
          "Authorization": `Bearer ${accessToken}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
            Title: newItem.Title, 
            genreId: newItem.genreId,
            releaseYear: parseInt(newItem.releaseYear, 10),
            duration: newItem.type === "movies" ? parseInt(newItem.duration, 10) : undefined,
            seasons: newItem.type === "series" ? parseInt(newItem.seasons, 10) : undefined,
            language: newItem.language
        }),              
      });
      if (!response.ok) {
        throw new Error("Error al crear contenido.");
      }
      const createdItem = await response.json();
      if (newItem.type === "movies") {
        setMovies([...movies, createdItem]);
      } else {
        setSeries([...series, createdItem]);
      }
      setIsAdding(false);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDelete = async (type, id) => {
    const accessToken = sessionStorage.getItem("accessToken");
    try {
      // Se usa el endpoint correspondiente y se pasa el ID correcto
      const response = await fetch(`${config.API_URL}/api/admin/${type}/${id}`, {
        method: "DELETE",
        headers: { "Authorization": `Bearer ${accessToken}` },
      });
      if (!response.ok) {
        throw new Error("Error al eliminar contenido.");
      }
      if (type === "movies") {
        setMovies(movies.filter(movie => movie.Id_Movie !== id));
      } else {
        setSeries(series.filter(serie => serie.Id_Series !== id));
      }
    } catch (err) {
      setError(err.message);
    }
  };

  const handleEdit = (item) => {
    const id = item.type === "movies" ? item.Id_Movie : item.Id_Series;
    setEditItem({ ...item, id });
    setIsEditing(true);
  };


  const handleEditInputChange = (e) => {
    const { name, value } = e.target;
    setEditItem({ ...editItem, [name]: value });
  };

  const handleUpdate = async () => {
    const accessToken = sessionStorage.getItem("accessToken");

    const updatedData = {
        Title: editItem.Title,
        genreId: parseInt(editItem.genreId, 10),
        releaseYear: parseInt(editItem.releaseYear, 10),
        duration: editItem.type === "movies" ? parseInt(editItem.duration, 10) : undefined,
        seasons: editItem.type === "series" ? parseInt(editItem.seasons, 10) : undefined,
        language: editItem.language,
    };

    console.log("📌 Datos enviados al backend:", updatedData); 

    try {
        const response = await fetch(`${config.API_URL}/api/admin/${editItem.type}/${editItem.id}`, {
            method: "PUT",
            headers: { 
                "Authorization": `Bearer ${accessToken}`,
                "Content-Type": "application/json"
            },
            body: JSON.stringify(updatedData)
        });

        if (!response.ok) {
            throw new Error("Error al actualizar contenido.");
        }

        setIsEditing(false);
    } catch (error) {
        console.error("❌ Error en actualización:", error);
        setError(error.message);
    }
};

  return (
    <div className="admin-container">
      <h1>Panel de Administración</h1>
      {error && <p className="error-message">{error}</p>}
      <input
        type="text"
        placeholder="Buscar título..."
        onChange={(e) => setSearchQuery(e.target.value.toLowerCase())}
        className="search-box"
      />

        {/* Sección de Películas */}
        <h2>Películas</h2>
        <button
        className="add-btn"
        onClick={() => {
            setIsAdding(true);
            setNewItem({
            Title: "",
            genreId: "",
            releaseYear: "",
            duration: "",
            language: "",
            type: "movies",
            });
        }}
        >
        Añadir Película
        </button>
        <table className="users-table">
        <thead>
            <tr>
            <th>ID</th>
            <th>Título</th>
            <th>Género</th>
            <th>Año</th>
            <th>Duración</th>
            <th>Idioma</th>
            <th>Acciones</th>
            </tr>
        </thead>
        <tbody>
    {movies.length > 0 && genres.length > 0 &&
        movies.map((movie) => (
            <tr key={movie.Id_Movie}>
                <td>{movie.Id_Movie}</td>
                <td>{movie.Title}</td>
                <td>{genres.find((g) => Number(g.Id_Genre) === Number(movie.genreId))?.Name || "Sin género"}</td>
                <td>{movie.releaseYear ? movie.releaseYear : "N/A"}</td>
                <td>{movie.duration ? `${movie.duration} min` : "N/A"}</td>
                <td>{movie.language ? movie.language : "No definido"}</td>
                <td>
                    <button onClick={() => handleEdit(movie)}>Modificar</button>
                    <button onClick={() => handleDelete("movies", movie.Id_Movie)}>Eliminar</button>
                </td>
            </tr>
        ))}
</tbody>

        </table>

        {/* Sección de Series */}
        <h2>Series</h2>
        <button
        className="add-btn"
        onClick={() => {
            setIsAdding(true);
            setNewItem({
            Title: "",
            genreId: "",
            releaseYear: "",
            seasons: "",
            language: "",
            type: "series",
            });
        }}
        >
        Añadir Serie
        </button>
        <table className="users-table">
        <thead>
            <tr>
            <th>ID</th>
            <th>Título</th>
            <th>Género</th>
            <th>Año</th>
            <th>Temporadas</th>
            <th>Idioma</th>
            <th>Acciones</th>
            </tr>
        </thead>
        <tbody>
            {series
            .filter((serie) => serie.Title && serie.Title.toLowerCase().includes(searchQuery))
            .map((serie) => (
                <tr key={serie.Id_Series}>
                <td>{serie.Id_Series}</td>
                <td>{serie.Title}</td>
                <td>
                    {genres.find((g) => g.Id_Genre === serie.genreId)?.Name || "Sin género"}
                </td>
                <td>{serie.releaseYear ? serie.releaseYear : "N/A"}</td> {/* ✅ Corregido */}
                <td>{serie.seasons ? serie.seasons : "N/A"}</td>
                <td>{serie.language ? serie.language : "No definido"}</td> {/* ✅ Corregido */}
                <td>
                    <button onClick={() => handleEdit(serie)}>Modificar</button>
                    <button onClick={() => handleDelete("series", serie.Id_Series)}>Eliminar</button>
                </td>
                </tr>
            ))}
        </tbody>
        </table>


        {/* Modal de Edición */}
        {isEditing && editItem && (
            <div className="modal">
                <div className="modal-content">
                    <h2>Editar {editItem.type === "movies" ? "Película" : "Serie"}</h2>
                    <label>Título:</label>
                    <input 
                        type="text"
                        name="Title"
                        value={editItem.Title || ""}
                        onChange={handleEditInputChange}
                    />

                    <label>Género:</label>
                    <select 
                        name="genreId" 
                        value={editItem.genreId || ""}
                        onChange={handleEditInputChange}
                    >
                        <option value="">Seleccionar género...</option>
                        {genres.map(genre => (
                            <option key={genre.Id_Genre} value={genre.Id_Genre}>
                                {genre.Name}
                            </option>
                        ))}
                    </select>

                    <label>Año de estreno:</label>
                    <input 
                        type="number"
                        name="releaseYear"
                        value={editItem.releaseYear || ""}
                        onChange={handleEditInputChange}
                    />

                    {editItem.type === "movies" ? (
                        <>
                            <label>Duración (min):</label>
                            <input 
                                type="number"
                                name="duration"
                                value={editItem.duration || ""}
                                onChange={handleEditInputChange}
                            />
                        </>
                    ) : (
                        <>
                            <label>Temporadas:</label>
                            <input 
                                type="number"
                                name="seasons"
                                value={editItem.seasons || ""}
                                onChange={handleEditInputChange}
                            />
                        </>
                    )}

                    <label>Idioma:</label>
                    <input 
                        type="text"
                        name="language"
                        value={editItem.language || ""}
                        onChange={handleEditInputChange}
                    />

                    <button className="update-btn" onClick={handleUpdate}>Guardar Cambios</button>
                    <button className="cancel-btn" onClick={() => setIsEditing(false)}>Cancelar</button>
                </div>
            </div>
        )}

      {/* Modal de Creación */}
      {isAdding && (
        <div className="modal">
          <div className="modal-content">
            <h2>
              Añadir {newItem.type === "movies" ? "Película" : "Serie"}
            </h2>
            <input
              type="text"
              name="Title"
              value={newItem.Title}
              onChange={(e) =>
                setNewItem({ ...newItem, Title: e.target.value })
              }
              placeholder="Título"
            />
            <select
              name="genreId"
              value={newItem.genreId}
              onChange={(e) =>
                setNewItem({ ...newItem, genreId: e.target.value })
              }
            >
              <option value="">Seleccionar género...</option>
              {genres.map((genre) => (
                <option key={genre.Id_Genre} value={genre.Id_Genre}>
                  {genre.Name}
                </option>
              ))}
            </select>
            <input
              type="number"
              name="releaseYear"
              value={newItem.releaseYear}
              onChange={(e) =>
                setNewItem({ ...newItem, releaseYear: e.target.value })
              }
              placeholder="Año de estreno"
            />
            {newItem.type === "movies" ? (
              <input
                type="number"
                name="duration"
                value={newItem.duration}
                onChange={(e) =>
                  setNewItem({ ...newItem, duration: e.target.value })
                }
                placeholder="Duración en minutos"
              />
            ) : (
              <input
                type="number"
                name="seasons"
                value={newItem.seasons}
                onChange={(e) =>
                  setNewItem({ ...newItem, seasons: e.target.value })
                }
                placeholder="Número de temporadas"
              />
            )}
            <input
              type="text"
              name="language"
              value={newItem.language}
              onChange={(e) =>
                setNewItem({ ...newItem, language: e.target.value })
              }
              placeholder="Idioma"
            />
            <button onClick={handleCreate}>Guardar</button>
            <button onClick={() => setIsAdding(false)}>Cancelar</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
