import React, { useState, useEffect } from "react";
import config from "../utils/config";
import "../styles/styles.css";
import Modal from "../components/Modal";
import 'font-awesome/css/font-awesome.min.css';

const Index = ({ setFavoriteMessage }) => {
  const [moviesList, setMoviesList] = useState([]);
  const [seriesList, setSeriesList] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Fetch películas y series
  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/movies`);
        const data = await response.json();
        setMoviesList(data);
      } catch (error) {
        console.error("❌ Error al cargar películas:", error);
      }
    };

    const fetchSeries = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/series`);
        const data = await response.json();
        setSeriesList(data);
      } catch (error) {
        console.error("❌ Error al cargar series:", error);
      }
    };

    fetchMovies();
    fetchSeries();
  }, []);

  // Función para mostrar el modal con detalles
  const handleClick = async (id, type) => {
    try {
      const response = await fetch(`${config.API_URL}/api/${type}/${id}`);
      const data = await response.json();
      setSelectedItem({ ...data, type });
      setIsModalOpen(true);
    } catch (error) {
      console.error(`❌ Error al cargar ${type}:`, error);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedItem(null);
  };

  // Función para manejar favoritos
  const handleFavoriteClick = async (itemId, type) => {
    const userStr = sessionStorage.getItem("user");
    if (!userStr) return;

    const user = JSON.parse(userStr);
    let favs = user.Favorites ? user.Favorites.split(",").map(fav => fav.trim()) : [];
    const isFavorite = favs.includes(String(itemId));

    const newFavState = !isFavorite;
    setFavoriteMessage(newFavState ? "Añadido a favoritos" : "Eliminado de favoritos");
    setTimeout(() => setFavoriteMessage(""), 2000);

    if (newFavState) {
      favs.push(String(itemId));
    } else {
      favs = favs.filter(fav => fav !== String(itemId));
    }
    user.Favorites = favs.join(",");
    sessionStorage.setItem("user", JSON.stringify(user));

    // Actualizar en el backend
    try {
      const response = await fetch(`${config.API_URL}/api/usuario/favorites`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          userId: user.id,
          itemId: String(itemId),
          isFavorite: newFavState,
        }),
      });
      if (!response.ok) throw new Error("Error actualizando favoritos");
      await response.json();
    } catch (error) {
      console.error("❌ Error actualizando favoritos:", error);
    }
  };

  // Subcomponente para cada tarjeta con el ícono de favoritos
  const ContentCard = ({ item, type }) => {
    const [isFavorite, setIsFavorite] = useState(false);

    useEffect(() => {
      const userStr = sessionStorage.getItem("user");
      if (userStr) {
        const user = JSON.parse(userStr);
        const itemId = item.Id_Movie || item.Id_Series;
        if (user.Favorites) {
          const favs = user.Favorites.split(",").map(fav => fav.trim());
          setIsFavorite(favs.includes(String(itemId)));
        }
      }
    }, [item]);

    return (
      <div
        className="content-card"
        onClick={() => handleClick(item.Id_Movie || item.Id_Series, type)}
      >
        <div className="card-image-container" style={{ position: "relative" }}>
          <img src={`${config.API_URL}${item.Image}`} alt={item.Title} />
          <div
            className="favorite-icon"
            onClick={(e) => {
              e.stopPropagation();
              handleFavoriteClick(item.Id_Movie || item.Id_Series, type);
              setIsFavorite(!isFavorite);
            }}
            style={{
              position: "absolute",
              top: "10px",
              left: "10px",
              cursor: "pointer",
            }}
          >
            <i
              className={`fa ${isFavorite ? "fa-star" : "fa-star-o"}`}
              style={{ fontSize: "24px", color: "gold" }}
            />
          </div>
        </div>
        <p>{item.Title}</p>
      </div>
    );
  };

  return (
    <div className="index-container">
      <h1>Bienvenido a Blockbuster</h1>
      <p>Explora nuestras series y películas.</p>

      <div className="content-section">
        <h2>Series Populares</h2>
        <div className="content-carousel">
          {seriesList.map((series) => (
            <ContentCard key={series.Id_Series} item={series} type="series" />
          ))}
        </div>
      </div>

      <div className="content-section">
        <h2>Películas Destacadas</h2>
        <div className="content-carousel">
          {moviesList.map((movie) => (
            <ContentCard key={movie.Id_Movie} item={movie} type="movies" />
          ))}
        </div>
      </div>

      {/* Modal para mostrar detalles de películas o series */}
      <Modal isOpen={isModalOpen} onClose={closeModal} data={selectedItem} setFavoriteMessage={setFavoriteMessage} />
    </div>
  );
};

export default Index;
