import React, { useEffect, useState } from "react";
import config from "../utils/config";
import "../styles/styles.css";

const AdminUsers = () => {
    const [users, setUsers] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [usersPerPage, setUsersPerPage] = useState(10);
    const [searchQuery, setSearchQuery] = useState("");
    const [error, setError] = useState("");
    const [selectedUser, setSelectedUser] = useState(null);
    const [editData, setEditData] = useState({ name: "", email: "", role: "" });

    useEffect(() => {
        const fetchUsers = async () => {
            const accessToken = sessionStorage.getItem("accessToken");

            if (!accessToken) {
                setError("❌ No hay token de autenticación. Inicia sesión nuevamente.");
                return;
            }

            try {
                const response = await fetch(`${config.API_URL}/api/admin/users`, {
                    method: "GET",
                    headers: { 
                        "Authorization": `Bearer ${accessToken}`,
                        "Content-Type": "application/json"
                    }
                });

                if (!response.ok) {
                    throw new Error("Error al obtener usuarios.");
                }

                const data = await response.json();
                setUsers(data);
            } catch (error) {
                setError(error.message);
            }
        };

        fetchUsers();
    }, []);

    const handleEditClick = (user) => {
        setSelectedUser(user);
        setEditData({ name: user.name, email: user.email, role: user.role });
    };

    const handleInputChange = (e) => {
        setEditData({ ...editData, [e.target.name]: e.target.value });
    };

    const handleUpdate = async () => {
        const accessToken = sessionStorage.getItem("accessToken");

        if (!accessToken) {
            setError("❌ No hay token de autenticación.");
            return;
        }

        try {
            const response = await fetch(`${config.API_URL}/api/admin/users/${selectedUser.id}`, {
                method: "PUT",
                headers: { 
                    "Authorization": `Bearer ${accessToken}`,
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(editData)
            });

            if (!response.ok) {
                throw new Error("Error al actualizar usuario.");
            }

            setUsers(users.map(user => user.id === selectedUser.id ? { ...user, ...editData } : user));
            setSelectedUser(null);
        } catch (error) {
            setError(error.message);
        }
    };

    const handleDelete = async (userId) => {
        const accessToken = sessionStorage.getItem("accessToken");

        if (!accessToken) {
            setError("❌ No hay token de autenticación.");
            return;
        }

        try {
            const response = await fetch(`${config.API_URL}/api/admin/users/${userId}`, {
                method: "DELETE",
                headers: { 
                    "Authorization": `Bearer ${accessToken}`,
                    "Content-Type": "application/json"
                }
            });

            if (!response.ok) {
                throw new Error("Error al eliminar usuario.");
            }

            setUsers(users.filter(user => user.id !== userId));
        } catch (error) {
            setError(error.message);
        }
    };

    const handlePageChange = (newPage) => setCurrentPage(newPage);
    const handleUsersPerPageChange = (e) => setUsersPerPage(Number(e.target.value));
    const handleSearchChange = (e) => setSearchQuery(e.target.value.toLowerCase());

    const filteredUsers = users.filter(user =>
        user.name.toLowerCase().includes(searchQuery) || user.email.toLowerCase().includes(searchQuery)
    );

    const indexOfLastUser = currentPage * usersPerPage;
    const indexOfFirstUser = indexOfLastUser - usersPerPage;
    const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);

    return (
        <div className="admin-container">
            <h1>Gestionar Usuarios</h1>
            {error && <p className="error-message">{error}</p>}

            {/* Buscador */}
            <input 
                type="text" 
                placeholder="Buscar usuario..." 
                onChange={handleSearchChange} 
                className="search-box"
            />

            <table className="users-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nombre</th>
                        <th>Correo</th>
                        <th>Rol</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {currentUsers.map(user => (
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>{user.role}</td>
                            <td>
                                <button className="edit-btn" onClick={() => handleEditClick(user)}>Modificar</button>
                                <button className="delete-btn" onClick={() => handleDelete(user.id)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Botones de paginación */}
            <div className="pagination">
                {Array.from({ length: Math.ceil(filteredUsers.length / usersPerPage) }, (_, index) => (
                    <button 
                        key={index} 
                        onClick={() => handlePageChange(index + 1)} 
                        className={currentPage === index + 1 ? "active" : ""}
                    >
                        {index + 1}
                    </button>
                ))}
            </div>

            

            {/* Formulario de edición emergente */}
            {selectedUser && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>Editar Usuario</h2>
                        <label>Nombre:</label>
                        <input type="text" name="name" value={editData.name} onChange={handleInputChange} />

                        <label>Correo:</label>
                        <input type="email" name="email" value={editData.email} onChange={handleInputChange} />

                        <label>Rol:</label>
                        <select name="role" value={editData.role} onChange={handleInputChange}>
                            <option value="admin">Admin</option>
                            <option value="user">Usuario</option>
                        </select>

                        <button className="update-btn" onClick={handleUpdate}>Guardar Cambios</button>
                        <button className="cancel-btn" onClick={() => setSelectedUser(null)}>Cancelar</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminUsers;
