import React, { useState, useEffect } from "react";
import config from "../utils/config";
import "../styles/styles.css";
import Modal from "../components/Modal";
import 'font-awesome/css/font-awesome.min.css';

const MiLista = ({ setFavoriteMessage }) => {
  const [favMovies, setFavMovies] = useState([]);
  const [favSeries, setFavSeries] = useState([]);
  const [moviesList, setMoviesList] = useState([]);
  const [seriesList, setSeriesList] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Cargar la lista completa de pel√≠culas y series
  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/movies`);
        if (!response.ok) throw new Error(`HTTP error! Status: ${response.status}`);
        const data = await response.json();
        setMoviesList(data);
      } catch (error) {
        console.error("‚ùå Error al cargar las pel√≠culas:", error);
      }
    };

    const fetchSeries = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/series`);
        if (!response.ok) throw new Error(`HTTP error! Status: ${response.status}`);
        const data = await response.json();
        setSeriesList(data);
      } catch (error) {
        console.error("‚ùå Error al cargar las series:", error);
      }
    };

    fetchMovies();
    fetchSeries();
  }, []);

  // Filtrar favoritos a partir de lo almacenado en sessionStorage
  useEffect(() => {
    const userStr = sessionStorage.getItem("user");
    if (userStr) {
      const user = JSON.parse(userStr);
      const favorites = user.Favorites?.split(",").map(fav => fav.trim()) || [];
      console.log("üìå Favorites guardados en sessionStorage:", favorites);

      const favM = moviesList.filter(movie => favorites.includes(String(movie.Id_Movie)));
      const favS = seriesList.filter(series => favorites.includes(String(series.Id_Series)));

      console.log("‚úÖ Pel√≠culas favoritas filtradas:", favM);
      console.log("‚úÖ Series favoritas filtradas:", favS);

      setFavMovies(favM);
      setFavSeries(favS);
    }
  }, [moviesList, seriesList]);

  const handleClick = async (id, type) => {
    try {
      const response = await fetch(`${config.API_URL}/api/${type}/${id}`);
      const data = await response.json();
      setSelectedItem({ ...data, type });
      setIsModalOpen(true);
    } catch (error) {
      console.error("‚ùå Error al cargar el detalle:", error);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedItem(null);
  };

  return (
    <div className="content-page">
      <h1>Mi Lista</h1>
      
      <div className="favorites-section">
        <h2>Pel√≠culas Favoritas</h2>
        <div className="content-carousel">
          {favMovies.length > 0 ? (
            favMovies.map(movie => (
              <div key={movie.Id_Movie} className="content-card" onClick={() => handleClick(movie.Id_Movie, "movies")}>
                <div className="card-image-container" style={{ position: "relative" }}>
                  <img src={`${config.API_URL}${movie.Image}`} alt={movie.Title} />
                </div>
                <p>{movie.Title}</p>
              </div>
            ))
          ) : (
            <p>No tienes pel√≠culas en tu lista.</p>
          )}
        </div>
      </div>

      <div className="favorites-section">
        <h2>Series Favoritas</h2>
        <div className="content-carousel">
          {favSeries.length > 0 ? (
            favSeries.map(series => (
              <div key={series.Id_Series} className="content-card" onClick={() => handleClick(series.Id_Series, "series")}>
                <div className="card-image-container" style={{ position: "relative" }}>
                  <img src={`${config.API_URL}${series.Image}`} alt={series.Title} />
                </div>
                <p>{series.Title}</p>
              </div>
            ))
          ) : (
            <p>No tienes series en tu lista.</p>
          )}
        </div>
      </div>

      <Modal isOpen={isModalOpen} onClose={closeModal} data={selectedItem} setFavoriteMessage={setFavoriteMessage} />
    </div>
  );
};

export default MiLista;
