import React, { useState, useEffect } from "react";
import config from "../utils/config";
import "../styles/styles.css";
import Modal from "../components/Modal";

const Movies = () => {
  const [moviesList, setMoviesList] = useState([]); // Estado para almacenar películas
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/movies`);
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();
        setMoviesList(data);
      } catch (error) {
        console.error("❌ Error al cargar las películas:", error);
      }
    };

    fetchMovies();
  }, []);

  const handleClick = async (movieId) => {
    try {
      const response = await fetch(`${config.API_URL}/api/movies/${movieId}`);
      const data = await response.json();
      setSelectedMovie({ ...data, type: "movies" });
      setIsModalOpen(true);
    } catch (error) {
      console.error("❌ Error al cargar la película:", error);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedMovie(null);
  };

  return (
    <div className="content-page">
      <h1>Películas</h1>

      <div className="content-carousel">
        {moviesList.map(movie => (
          <div key={movie.Id_Movie} className="content-card" onClick={() => handleClick(movie.Id_Movie)}>
            <img src={`${config.API_URL}${movie.Image}`} alt={movie.Title} />
            <p>{movie.Title}</p>
          </div>
        ))}
      </div>

      <Modal isOpen={isModalOpen} onClose={closeModal} data={selectedMovie} />
    </div>
  );
};

export default Movies;
