import React, { useState, useEffect } from "react";
import config from "../utils/config";
import "../styles/styles.css";
import Modal from "../components/Modal";
import 'font-awesome/css/font-awesome.min.css';

const Movies = ({ setFavoriteMessage }) => {
  const [moviesList, setMoviesList] = useState([]); // Lista de películas
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/movies`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setMoviesList(data);
      } catch (error) {
        console.error("❌ Error al cargar las películas:", error);
      }
    };
    fetchMovies();
  }, []);

  const handleClick = async (movieId) => {
    try {
      const response = await fetch(`${config.API_URL}/api/movies/${movieId}`);
      const data = await response.json();
      setSelectedMovie({ ...data, type: "movies" });
      setIsModalOpen(true);
    } catch (error) {
      console.error("❌ Error al cargar la película:", error);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedMovie(null);
  };

  // Subcomponente ContentCard para cada película
  const ContentCard = ({ item }) => {
    // Usamos el ID numérico directamente sin prefijo
    const favoriteKey = String(item.Id_Movie);
    const [isFavorite, setIsFavorite] = useState(false);
    const [message, setMessage] = useState("");

    console.log("🔎 ID de favorito generado:", favoriteKey);

    useEffect(() => {
      const userStr = sessionStorage.getItem("user");
      if (userStr) {
        const user = JSON.parse(userStr);
        const favorites = user.Favorites?.split(",").map(fav => fav.trim()) || [];
        console.log("📌 Favoritos actuales en sessionStorage:", favorites);
        setIsFavorite(favorites.includes(favoriteKey));
      }
    }, [item, favoriteKey]);

const handleFavoriteClick = async (e) => {
  e.stopPropagation();
  const newFavState = !isFavorite;
  setIsFavorite(newFavState);

  console.log("📌 Antes de modificar favoritos:", sessionStorage.getItem("user"));

  // Establecer mensaje flotante global y local
  setFavoriteMessage(newFavState ? "Añadido a favoritos" : "Eliminado de favoritos");
  setTimeout(() => setFavoriteMessage(""), 2000);
  setMessage(newFavState ? "Añadido a favoritos" : "Eliminado de favoritos");
  setTimeout(() => setMessage(""), 2000);

  // Actualizar sessionStorage (limpiando el campo Favorites si contiene 'undefined')
  const userStr = sessionStorage.getItem("user");
  if (userStr) {
    const user = JSON.parse(userStr);
    // Si Favorites contiene "undefined", se reinicia a cadena vacía.
    if (!user.Favorites || user.Favorites === "undefined") {
      user.Favorites = "";
    }
    let favs = user.Favorites
      ? user.Favorites.split(",").map((f) => f.trim()).filter((f) => f !== "" && f !== "undefined")
      : [];
    if (newFavState) {
      if (!favs.includes(favoriteKey)) {
        favs.push(favoriteKey);
      }
    } else {
      favs = favs.filter((f) => f !== favoriteKey);
    }
    user.Favorites = favs.join(",");
    sessionStorage.setItem("user", JSON.stringify(user));
    console.log("📌 Después de modificar favoritos:", JSON.parse(sessionStorage.getItem("user")));
  }

  // Enviar actualización al backend usando la propiedad Id_User en lugar de id
  try {
    const userObj = JSON.parse(sessionStorage.getItem("user"));
    const requestBody = {
      userId: userObj.Id_User,  // Cambiado de userObj.id a userObj.Id_User
      itemKey: favoriteKey,
      isFavorite: newFavState,
    };

    console.log("📤 Datos enviados al backend:", requestBody);

    const response = await fetch(`${config.API_URL}/api/usuario/favorites`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(requestBody),
    });

    if (!response.ok) {
      throw new Error(`Error actualizando favoritos (Código: ${response.status})`);
    }

    const responseData = await response.json();
    console.log("✅ Respuesta del backend:", responseData);

    // Recargar usuario desde el backend para sincronizar sessionStorage
    const updatedUserResponse = await fetch(`${config.API_URL}/api/usuario/${userObj.Id_User}`);
    if (updatedUserResponse.ok) {
      const updatedUserData = await updatedUserResponse.json();
      sessionStorage.setItem("user", JSON.stringify(updatedUserData));
      console.log("🔄 sessionStorage actualizado después del PUT:", updatedUserData);
    } else {
      console.error("❌ Error recargando usuario desde el backend");
    }
  } catch (error) {
    console.error("❌ Error actualizando favoritos:", error);
  }
};

    return (
      <div className="content-card" onClick={() => handleClick(item.Id_Movie)}>
        <div className="card-image-container" style={{ position: "relative" }}>
          <img src={`${config.API_URL}${item.Image}`} alt={item.Title} />
          <div
            className="favorite-icon"
            onClick={handleFavoriteClick}
            style={{
              position: "absolute",
              top: "10px",
              left: "10px",
              cursor: "pointer",
            }}
          >
            <i
              className={`fa ${isFavorite ? "fa-star" : "fa-star-o"}`}
              style={{ fontSize: "24px", color: "gold" }}
            />
            {message && (
              <div
                style={{
                  position: "absolute",
                  top: "0",
                  left: "40px",
                  background: "rgba(0,0,0,0.7)",
                  padding: "3px 8px",
                  borderRadius: "5px",
                  color: "white",
                  fontSize: "14px",
                }}
              >
                {message}
              </div>
            )}
          </div>
        </div>
        <p>{item.Title}</p>
      </div>
    );
  };

  return (
    <div className="content-page">
      <h1>Películas</h1>
      <div className="content-carousel">
        {moviesList.map((movie) => (
          <ContentCard key={movie.Id_Movie} item={movie} />
        ))}
      </div>
      {/* Modal para mostrar detalles */}
      <Modal isOpen={isModalOpen} onClose={closeModal} data={selectedMovie} setFavoriteMessage={setFavoriteMessage} />
    </div>
  );
};

export default Movies;
