import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import config from "../utils/config";
import "../styles/styles.css";

const Register = () => {
  const [user, setUser] = useState({
    name: "",
    email: "",
    address: "",
    password: "",
    confirmPassword: "",
    avatar: "", 
    subscription: "", 
  });


  const [errorMessage, setErrorMessage] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  // üîπ Lista de avatares con im√°genes almacenadas en la carpeta p√∫blica del backend
  const avatarList = [
    { id: 0, url: `${config.API_URL}/uploads/avatars/7309681.jpg`, description: "Avatar1" },
    { id: 1, url: `${config.API_URL}/uploads/avatars/9334176.jpg`, description: "Avatar2" },
    { id: 2, url: `${config.API_URL}/uploads/avatars/10491830.jpg`, description: "Avatar3" }
  ];

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleAvatarSelect = (avatarUrl) => {
    setSelectedAvatar(avatarUrl);
    setUser({ ...user, avatar: avatarUrl }); // üîπ Guardamos la URL en el estado 'user'
  };

  const validatePassword = (password) => {
    const regex = /^(?=.*[A-Z])(?=.*[a-z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    return regex.test(password);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage("");

    if (user.password !== user.confirmPassword) {
      setErrorMessage("Las contrase√±as no coinciden.");
      return;
    }

    if (!validatePassword(user.password)) {
      setErrorMessage("La contrase√±a debe tener al menos 1 may√∫scula, 1 min√∫scula, 1 n√∫mero y 1 car√°cter especial.");
      return;
    }

    if (!user.avatar) {
      setErrorMessage("Debes seleccionar un avatar.");
      return;
    }

    if (!user.subscription) {
      setErrorMessage("Debes seleccionar un tipo de suscripci√≥n.");
      return;
    }

    try {
      const response = await fetch(`${config.API_URL}/register`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(user),
      });

      if (!response.ok) {
        const { message } = await response.json();
        throw new Error(message || "Error al registrar. Int√©ntalo de nuevo.");
      }

      alert("Registro exitoso.");
      navigate("/login", { replace: true });
    } catch (error) {
      setErrorMessage(error.message);
    }
  };

  return (
    <div className="form-container">
      <div className="form-box">
        <h2>Registro</h2>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <form onSubmit={handleSubmit}>
          <input type="text" name="name" placeholder="Nombre" onChange={handleChange} required />
          <input type="email" name="email" placeholder="Correo" onChange={handleChange} required />
          <input type="text" name="address" placeholder="Direcci√≥n" onChange={handleChange} required />

          {/* üîπ Campo de selecci√≥n de suscripci√≥n */}
          <select name="subscription" onChange={handleChange} required>
            <option value="">Selecciona un plan</option>
            <option value="Mensual">Mensual - 9,99 ‚Ç¨</option>
            <option value="Trimestral">Trimestral - 27,99 ‚Ç¨</option>
            <option value="Anual">Anual - 109,99 ‚Ç¨</option>
          </select>

          <div className="password-container">
            <input type={showPassword ? "text" : "password"} name="password" placeholder="Contrase√±a" onChange={handleChange} required />
          </div>
          <div className="password-container">
            <input type="password" name="confirmPassword" placeholder="Repetir Contrase√±a" onChange={handleChange} required />
          </div>
          <button type="button" className="toggle-password" onClick={() => setShowPassword(!showPassword)}>
            {showPassword ? "Ocultar" : "Mostrar"}
          </button>

          <button type="submit">Registrar</button>
        </form>
        <p>¬øYa tienes cuenta? <a href="/login">Inicia sesi√≥n aqu√≠</a></p>
      </div>
    </div>
  );
};

export default Register;
