import React, { useEffect, useState } from "react";
import config from "../utils/config";
import "../styles/styles.css";

const AdminPanel = () => {
    const [movies, setMovies] = useState([]);
    const [series, setSeries] = useState([]);
    const [genres, setGenres] = useState([]); // 🔹 Nuevo estado para géneros
    const [currentMoviePage, setCurrentMoviePage] = useState(1);
    const [currentSeriesPage, setCurrentSeriesPage] = useState(1);
    const [itemsPerPage, setItemsPerPage] = useState(10);
    const [searchQuery, setSearchQuery] = useState("");
    const [selectedItem, setSelectedItem] = useState(null);
    const [editData, setEditData] = useState({});
    const [error, setError] = useState("");
    const [isAdding, setIsAdding] = useState(false);
    const [newItem, setNewItem] = useState({ title: "", genreId: "", releaseYear: "", duration: "", language: "", type: "movies" });

    useEffect(() => {
        const fetchData = async () => {
            const accessToken = sessionStorage.getItem("accessToken");

            if (!accessToken) {
                setError("❌ No hay token de autenticación. Inicia sesión nuevamente.");
                return;
            }

            try {
                const moviesResponse = await fetch(`${config.API_URL}/api/admin/movies`, {
                    method: "GET",
                    headers: { "Authorization": `Bearer ${accessToken}`, "Content-Type": "application/json" }
                });

                const seriesResponse = await fetch(`${config.API_URL}/api/admin/series`, {
                    method: "GET",
                    headers: { "Authorization": `Bearer ${accessToken}`, "Content-Type": "application/json" }
                });

                const genresResponse = await fetch(`${config.API_URL}/api/admin/genres`, {
                    method: "GET",
                    headers: { "Authorization": `Bearer ${accessToken}`, "Content-Type": "application/json" }
                });

                if (!moviesResponse.ok || !seriesResponse.ok || !genresResponse.ok) {
                    throw new Error("Error al obtener datos.");
                }

                setMovies(await moviesResponse.json());
                setSeries(await seriesResponse.json());
                setGenres(await genresResponse.json()); // 🔹 Guardar géneros en el estado
            } catch (error) {
                setError(error.message);
            }
        };

        fetchData();
    }, []);

    const handleSearchChange = (e) => {
        setSearchQuery(e.target.value.toLowerCase());
    };

    const handleCreate = async () => {
        console.log("📌 handleCreate activado con:", newItem);
        const accessToken = sessionStorage.getItem("accessToken");
    
        if (!accessToken) {
            setError("❌ No hay token de autenticación.");
            return;
        }
    
        try {
            const response = await fetch(`${config.API_URL}/api/admin/${newItem.type}`, {
                method: "POST",
                headers: { 
                    "Authorization": `Bearer ${accessToken}`,
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(newItem) // 🔹 Ahora envía `genreId`, no `genreName`
            });
    
            console.log("📌 Solicitud enviada al backend:", JSON.stringify(newItem));
    
            if (!response.ok) {
                throw new Error("Error al crear contenido.");
            }
    
            const createdItem = await response.json();
    
            if (newItem.type === "movies") {
                setMovies([...movies, createdItem]);
            } else {
                setSeries([...series, createdItem]);
            }
    
            setIsAdding(false);
        } catch (error) {
            console.error("❌ Error al crear contenido:", error);
            setError(error.message);
        }
    };    

    const handleInputChange = (e) => {
        setNewItem({ ...newItem, [e.target.name]: e.target.value });
    };

    return (
        <div className="admin-container">
            <h1>Panel de Administración</h1>
            {error && <p className="error-message">{error}</p>}

            <input 
                type="text" 
                placeholder="Buscar título..." 
                onChange={handleSearchChange} 
                className="search-box"
            />

            <h2>Películas</h2>
            <button className="add-btn" onClick={() => { 
                console.log("📌 Botón Añadir presionado"); 
                setIsAdding(true); 
                setNewItem({ title: "", genreId: "", releaseYear: "", duration: "", language: "", type: "movies" }); 
            }}>
                Añadir Película
            </button>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Título</th>
                        <th>Género</th>
                        <th>Año</th>
                        <th>Duración</th>
                        <th>Idioma</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {movies.filter(movie => movie?.Title?.toLowerCase().includes(searchQuery || "")).map(movie => (
                        <tr key={movie.Id_Movie}>
                            <td>{movie.Id_Movie}</td>
                            <td>{movie.Title}</td>
                            <td>{movie.Genre}</td>
                            <td>{movie.Release_Year}</td>
                            <td>{movie.Duration} min</td>
                            <td>{movie.Language}</td>
                            <td>
                                <button className="edit-btn">Modificar</button>
                                <button className="delete-btn">Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            
            <h2>Series</h2>
            <button className="add-btn" onClick={() => { 
                console.log("📌 Botón Añadir Serie presionado"); 
                setIsAdding(true); 
                setNewItem({ title: "", genre: "", releaseYear: "", seasons: "", language: "", type: "series" }); 
            }}>
                Añadir Serie
            </button>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Título</th>
                        <th>Género</th>
                        <th>Año</th>
                        <th>Temporadas</th>
                        <th>Idioma</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                {series.filter(serie => serie?.Title?.toLowerCase().includes(searchQuery || "")).map(serie => (

                        <tr key={serie.Id_Series}>
                            <td>{serie.Id_Series}</td>
                            <td>{serie.Title}</td>
                            <td>{serie.Genre}</td>
                            <td>{serie.Release_Year}</td>
                            <td>{serie.Seasons}</td>
                            <td>{serie.Language}</td>
                            <td>
                                <button className="edit-btn">Modificar</button>
                                <button className="delete-btn">Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

                        {/* Modal para añadir contenido */}
            {isAdding && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>Añadir {newItem.type === "movies" ? "Película" : "Serie"}</h2>
                        <label>Título:</label>
                        <input type="text" name="title" value={newItem.title} onChange={handleInputChange} />

                        <label>Género:</label>
                        <select name="genreId" value={newItem.genreId} onChange={handleInputChange}>
                            <option value="">Seleccionar género...</option>
                            {genres.map(genre => (
                                <option key={genre.Id_Genre} value={genre.Id_Genre}>
                                    {genre.Name}
                                </option>
                            ))}
                        </select>

                        <label>Año de estreno:</label>
                        <input type="number" name="releaseYear" value={newItem.releaseYear} onChange={handleInputChange} />

                        <label>Duración (minutos):</label>
                        <input type="number" name="duration" value={newItem.duration} onChange={handleInputChange} />

                        <label>Idioma:</label>
                        <input type="text" name="language" value={newItem.language} onChange={handleInputChange} />

                        <button className="save-btn" onClick={handleCreate}>Guardar</button>
                        <button className="cancel-btn" onClick={() => setIsAdding(false)}>Cancelar</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminPanel;
