import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import config from "../utils/config";
import "../styles/userProfile.css";

const UserProfile = () => {
  const { id } = useParams();
  const [userData, setUserData] = useState(null);
  const [favoriteItems, setFavoriteItems] = useState([]);

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/usuario/${id}`);
        if (!response.ok) {
          console.error("Respuesta no OK, status:", response.status);
        }
        const data = await response.json();
        console.log("ðŸ“Œ Datos de usuario obtenidos desde API:", data);
        setUserData(data);
      } catch (error) {
        console.error("âŒ Error al cargar perfil de usuario:", error);
      }
    };

    fetchUserProfile();
  }, [id]);

  useEffect(() => {
    const fetchFavorites = async () => {
      if (userData && userData.Favorites) {
        const favIds = userData.Favorites.split(",").map(fav => fav.trim()).filter(fav => fav !== "");

        console.log("ðŸ“Œ IDs de favoritos obtenidos:", favIds);

        const favPromises = favIds.map(async (favId) => {
          let item = null;
          try {
            const resMovie = await fetch(`${config.API_URL}/api/movies/${favId}`);
            if (resMovie.ok) {
              const dataMovie = await resMovie.json();
              item = { ...dataMovie, type: "movie" };
            }
          } catch (e) {
            console.error(`âŒ Error al cargar pelÃ­cula con ID ${favId}:`, e);
          }
          if (!item) {
            try {
              const resSeries = await fetch(`${config.API_URL}/api/series/${favId}`);
              if (resSeries.ok) {
                const dataSeries = await resSeries.json();
                item = { ...dataSeries, type: "series" };
              }
            } catch (e) {
              console.error(`âŒ Error al cargar serie con ID ${favId}:`, e);
            }
          }
          return item;
        });

        const favResults = await Promise.all(favPromises);
        setFavoriteItems(favResults.filter(item => item !== null));

        console.log("âœ… Lista final de favoritos:", favResults.filter(item => item !== null));
      }
    };

    fetchFavorites();
  }, [userData]);

  if (!userData) {
    return <p className="loading">Cargando perfil...</p>;
  }

  return (
    <div className="userProfile-container">
      <h2>{userData.Name}</h2>
      <p>{userData.Email}</p>
      <h3>Favoritos</h3>
      <div className="favorites-gallery">
        {favoriteItems.length > 0 ? (
          favoriteItems.map((fav, index) => (
            <img key={index} src={`${config.API_URL}${fav.Image}`} alt={fav.Title || "Favorito"} className="favorite-image" />
          ))
        ) : (
          <p>No tienes favoritos.</p>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
