import React, { useState } from "react";
import { useNavigate, Navigate } from "react-router-dom";
import config from "../utils/config";
import "../styles/styles.css";

const Login = () => {
  const [credentials, setCredentials] = useState({ email: "", password: "" });
  const [errorMessage, setErrorMessage] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [redirect, setRedirect] = useState(false);
  const navigate = useNavigate();

  if (redirect) {
    console.log("📌 Redirigiendo ahora...");
    return <Navigate to="/index" />;
  }

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage("");

    try {
      const response = await fetch(`${config.API_URL}/api/login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(credentials),
      });

      console.log("📌 Respuesta cruda del servidor:", response);
      const responseData = await response.json();
      console.log("📌 JSON recibido desde el backend:", responseData);

      if (!response.ok) {
        throw new Error(responseData.message || "Error al iniciar sesión.");
      }

      const { token, user } = responseData;
      if (!token) {
        console.error("❌ No se recibió un token desde el backend.");
        return;
      }

      console.log("📌 Usuario recibido en login:", user);
      console.log("📌 Avatar recibido:", user.avatar);

      // Verificamos y normalizamos el avatar
      if (!user.avatar || user.avatar === "undefined") {
        console.warn("⚠️ Avatar recibido es undefined, asignando por defecto.");
        user.avatar = `${config.API_URL}/uploads/avatars/default-avatar.jpg`;
      } else if (!user.avatar.startsWith("http")) {
        user.avatar = `${config.API_URL}${user.avatar}`;
      }
      console.log("📌 Avatar normalizado:", user.avatar);

      // Es fundamental que el objeto usuario esté almacenado de forma consistente.
      // Por ejemplo, asegurarse de que el campo Favorites esté definido (aunque vacío).
      if (!user.Favorites || user.Favorites === "undefined") {
        user.Favorites = "";
      }

      // Guardamos token y usuario en localStorage para que persistan incluso si se recarga o
      // se cierra el navegador
      localStorage.setItem("accessToken", token);
      localStorage.setItem("user", JSON.stringify(user));

      console.log(
        "✅ Token guardado en localStorage:",
        localStorage.getItem("accessToken")
      );
      console.log("✅ Usuario guardado en localStorage:", localStorage.getItem("user"));

      console.log("📌 Redirigiendo a index...");
      navigate("/index");

      setTimeout(() => {
        if (window.location.pathname !== "/index") {
          console.warn("⚠️ Redirección con navigate fallida, usando window.location...");
          window.location.href = "/index";
        }
      }, 500);
    } catch (error) {
      console.error("❌ Error en el login:", error);
      setErrorMessage(error.message);
    }
  };

  return (
    <div className="form-container">
      <div className="form-box">
        <h2>Iniciar Sesión</h2>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            name="email"
            placeholder="Correo"
            onChange={handleChange}
            required
          />
          <div className="password-container">
            <input
              type={showPassword ? "text" : "password"}
              name="password"
              placeholder="Contraseña"
              onChange={handleChange}
              required
            />
            <button
              type="button"
              className="toggle-password"
              onClick={() => setShowPassword(!showPassword)}
            >
              {showPassword ? "Ocultar" : "Mostrar"}
            </button>
          </div>
          <button type="submit">Ingresar</button>
        </form>
        <p>
          ¿No tienes cuenta? <a href="/register">Regístrate aquí</a>
        </p>
      </div>
    </div>
  );
};

export default Login;
