import React, { useEffect, useState } from "react";
import config from "../utils/config";
import "../styles/styles.css";

const AdminUsers = () => {
    const [users, setUsers] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [usersPerPage, setUsersPerPage] = useState(10);
    const [searchQuery, setSearchQuery] = useState("");
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchUsers = async () => {
            const accessToken = sessionStorage.getItem("accessToken");

            if (!accessToken) {
                console.error("No hay token en sessionStorage. ¿El usuario está autenticado?");
                setError("No hay token de autenticación. Inicia sesión nuevamente.");
                return;
            }
            
            console.log("📌 Token enviado en la solicitud:", accessToken);
            
            const response = await fetch(`${config.API_URL}/api/admin/users`, {
                method: "GET",
                headers: { 
                    "Authorization": `Bearer ${accessToken}`,
                    "Content-Type": "application/json"
                }
            });

            try {
                const response = await fetch(`${config.API_URL}/api/admin/users`, {
                    method: "GET",
                    headers: { 
                        "Authorization": `Bearer ${accessToken}`,
                        "Content-Type": "application/json"
                    }
                });

                console.log("📌 Respuesta cruda del servidor:", response);

                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.message || "Error al obtener usuarios.");
                }

                const data = await response.json();
                setUsers(data);
            } catch (error) {
                console.error("❌ Error en fetchUsers:", error);
                setError(error.message);
            }
        };

        fetchUsers();
    }, []);

    const handleDelete = async (userId) => {
        const accessToken = sessionStorage.getItem("accessToken");

        if (!accessToken) {
            console.error("❌ No se encontró un token en sessionStorage.");
            setError("❌ No hay token de autenticación.");
            return;
        }

        try {
            const response = await fetch(`${config.API_URL}/api/admin/users/${userId}`, {
                method: "DELETE",
                headers: { 
                    "Authorization": `Bearer ${accessToken}`,
                    "Content-Type": "application/json"
                }
            });

            console.log("📌 Respuesta DELETE del servidor:", response);

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || "Error al eliminar usuario.");
            }

            setUsers(users.filter(user => user.id !== userId));
        } catch (error) {
            console.error("❌ Error en handleDelete:", error);
            setError(error.message);
        }
    };

    return (
        <div className="admin-container">
            <h1>Gestionar Usuarios</h1>
            {error && <p className="error-message">{error}</p>}
            <table className="users-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nombre</th>
                        <th>Correo</th>
                        <th>Rol</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>{user.role}</td>
                            <td>
                                <button className="delete-btn" onClick={() => handleDelete(user.id)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AdminUsers;
