import React, { useState, useEffect } from "react";
import config from "../utils/config";
import "../styles/styles.css";
import Modal from "../components/Modal";
import "font-awesome/css/font-awesome.min.css";

const Index = ({ setFavoriteMessage }) => {
  const [moviesList, setMoviesList] = useState([]);
  const [seriesList, setSeriesList] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Fetch pel√≠culas y series
  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/movies`);
        if (!response.ok) throw new Error(`HTTP error! Status: ${response.status}`);
        const data = await response.json();
        setMoviesList(data);
      } catch (error) {
        console.error("‚ùå Error al cargar pel√≠culas:", error);
      }
    };

    const fetchSeries = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/series`);
        if (!response.ok) throw new Error(`HTTP error! Status: ${response.status}`);
        const data = await response.json();
        setSeriesList(data);
      } catch (error) {
        console.error("‚ùå Error al cargar series:", error);
      }
    };

    fetchMovies();
    fetchSeries();
  }, []);

  // Funci√≥n para mostrar el modal con detalles
  const handleClick = async (id, type) => {
    try {
      const response = await fetch(`${config.API_URL}/api/${type}/${id}`);
      const data = await response.json();
      setSelectedItem({ ...data, type });
      setIsModalOpen(true);
    } catch (error) {
      console.error(`‚ùå Error al cargar ${type}:`, error);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedItem(null);
  };

  // Subcomponente para cada tarjeta (pel√≠cula o serie)
  const ContentCard = ({ item, type }) => {
    const [isFavorite, setIsFavorite] = useState(false);
    const [message, setMessage] = useState("");
    // Definir favoriteKey seg√∫n si es pel√≠cula o serie
    const favoriteKey = String(item.Id_Movie || item.Id_Series);

    useEffect(() => {
      const userStr = sessionStorage.getItem("user");
      if (userStr) {
        const user = JSON.parse(userStr);
        if (user.Favorites) {
          const favs = user.Favorites.split(",").map(fav => fav.trim());
          setIsFavorite(favs.includes(favoriteKey));
        }
      }
    }, [item, favoriteKey]);

    // Esta funci√≥n se declara aqu√≠ para usar los estados del ContentCard
    const handleFavoriteClick = async (e) => {
      e.stopPropagation();
      const newFavState = !isFavorite;
      setIsFavorite(newFavState);

      // Mostrar mensaje al usuario
      setFavoriteMessage(newFavState ? "A√±adido a favoritos" : "Eliminado de favoritos");
      setTimeout(() => setFavoriteMessage(""), 2000);
      setMessage(newFavState ? "A√±adido a favoritos" : "Eliminado de favoritos");
      setTimeout(() => setMessage(""), 2000);

      // Actualizar sessionStorage
      const userStr = sessionStorage.getItem("user");
      if (userStr) {
        const user = JSON.parse(userStr);
        let favs = user.Favorites 
          ? user.Favorites.split(",").map(f => f.trim()).filter(f => f !== "")
          : [];
        if (newFavState) {
          if (!favs.includes(favoriteKey)) favs.push(favoriteKey);
        } else {
          favs = favs.filter(f => f !== favoriteKey);
        }
        user.Favorites = favs.join(",");
        sessionStorage.setItem("user", JSON.stringify(user));
      }

      // Enviar actualizaci√≥n al backend usando la propiedad Id_User
      try {
        const userObj = JSON.parse(sessionStorage.getItem("user"));
        const requestBody = {
          userId: userObj.Id_User, // Usamos Id_User; aseg√∫rate de que en sessionStorage venga as√≠.
          itemKey: favoriteKey,
          isFavorite: newFavState,
        };

        console.log("üì§ Datos enviados al backend:", requestBody);

        const response = await fetch(`${config.API_URL}/api/usuario/favorites`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(requestBody),
        });
        if (!response.ok) {
          throw new Error(`Error actualizando favoritos (C√≥digo: ${response.status})`);
        }
        const responseData = await response.json();
        console.log("‚úÖ Respuesta del backend:", responseData);

        // Recargar el usuario desde el backend para actualizar sessionStorage
        const updatedUserResponse = await fetch(`${config.API_URL}/api/usuario/${userObj.Id_User}`);
        if (updatedUserResponse.ok) {
          const updatedUserData = await updatedUserResponse.json();
          sessionStorage.setItem("user", JSON.stringify(updatedUserData));
          window.dispatchEvent(new CustomEvent("favoritesUpdated"));
        } else {
          console.error("‚ùå Error recargando usuario desde el backend");
        }
      } catch (error) {
        console.error("‚ùå Error actualizando favoritos:", error);
      }
    };

    return (
      <div
        className="content-card"
        onClick={() => handleClick(favoriteKey, type)}
      >
        <div className="card-image-container" style={{ position: "relative" }}>
          <img src={`${config.API_URL}${item.Image}`} alt={item.Title} />
          <div
            className="favorite-icon"
            onClick={(e) => handleFavoriteClick(e)}
            style={{
              position: "absolute",
              top: "10px",
              left: "10px",
              cursor: "pointer",
            }}
          >
            <i
              className={`fa ${isFavorite ? "fa-star" : "fa-star-o"}`}
              style={{ fontSize: "24px", color: "gold" }}
            />
            {message && (
              <div
                style={{
                  position: "absolute",
                  top: "0",
                  left: "40px",
                  background: "rgba(0,0,0,0.7)",
                  padding: "3px 8px",
                  borderRadius: "5px",
                  color: "white",
                  fontSize: "14px",
                }}
              >
                {message}
              </div>
            )}
          </div>
        </div>
        <p>{item.Title}</p>
      </div>
    );
  };

  return (
    <div className="index-container">
      <h1>Bienvenido a Blockbuster</h1>
      <p>Explora nuestras series y pel√≠culas.</p>

      <div className="content-section">
        <h2>Series Populares</h2>
        <div className="content-carousel">
          {seriesList.map((series) => (
            <ContentCard key={series.Id_Series} item={series} type="series" />
          ))}
        </div>
      </div>

      <div className="content-section">
        <h2>Pel√≠culas Destacadas</h2>
        <div className="content-carousel">
          {moviesList.map((movie) => (
            <ContentCard key={movie.Id_Movie} item={movie} type="movies" />
          ))}
        </div>
      </div>

      <Modal
        isOpen={isModalOpen}
        onClose={closeModal}
        data={selectedItem}
        setFavoriteMessage={setFavoriteMessage}
      />
    </div>
  );
};

export default Index;
