import React from "react";
import "../styles/modal.css";

const Modal = ({ isOpen, onClose, data }) => {
    if (!isOpen) return null;
  
    return (
      <div className="modal-overlay" onClick={onClose}>
        <div className="modal-content" onClick={(e) => e.stopPropagation()}>
          <button className="close-button" onClick={onClose}>X</button>
          <h2>{data?.Title}</h2> {/* Cambiado de 'title' a 'Title' */}
          <img src={`${config.API_URL}${data?.Image}`} alt={data?.Title} /> {/* 'img' ahora es 'Image' */}
          <p><strong>Año de lanzamiento:</strong> {data?.Release_Year}</p> {/* 'release_year' ahora es 'Release_Year' */}
          <p><strong>Duración:</strong> {data?.Duration} min</p>
          <p><strong>Sinopsis:</strong> {data?.Synopsis}</p>
          <p><strong>Temporadas:</strong> {data?.Seasons}</p>
        </div>
      </div>
    );
  };  

export default Modal;
