import React, { useState, useEffect } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import config from "../utils/config";

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation();
const isAuthPage =
  location.pathname === "/" ||
  location.pathname === "/login" ||
  location.pathname === "/register";

  const [menuOpen, setMenuOpen] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = sessionStorage.getItem("user");
    console.log("üìå Datos en sessionStorage antes de procesar:", storedUser);

    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);

      console.log("üìå Usuario en Header:", parsedUser);
      console.log("üìå Avatar en Header antes de procesar:", parsedUser.avatar);

      // Si el avatar no es una URL completa, se le concatena la base
      if (parsedUser.avatar && !parsedUser.avatar.startsWith("http")) {
        parsedUser.avatar = `${config.API_URL}/${parsedUser.avatar}`;
      }

      // Si el avatar no est√° definido, se asigna uno por defecto
      parsedUser.avatar =
        parsedUser.avatar ||
        `${config.API_URL}/uploads/avatars/default-avatar.jpg`;

      console.log("üìå Avatar en Header despu√©s de procesar:", parsedUser.avatar);

      setUser(parsedUser);
    } else {
      setUser(null);
    }
  }, [location]);

  const handleLogout = () => {
    sessionStorage.removeItem("accessToken");
    sessionStorage.removeItem("user");
    localStorage.removeItem("refreshToken");
    navigate("/login");
  };

  return (
    <header className="header">
      <div className="logo-container">
        {isAuthPage ? (
          // Cuando est√© en login o register, se muestra el logo sin enlace
          <img
            src={`${config.API_URL}/uploads/assets/logo.jpg`}
            alt="Blockbuster Logo"
            className="logo"
          />
        ) : (
          // En el resto de rutas, el logo es clickeable y redirige a "/index"
          <Link to="/index" className="logo">
            <img
              src={`${config.API_URL}/uploads/assets/logo.jpg`}
              alt="Blockbuster Logo"
            />
          </Link>
        )}
      </div>

      {!isAuthPage && (
        <>
          <nav className="nav-links">
            <Link to="/series">Series</Link>
            <Link to="/peliculas">Pel√≠culas</Link>
            <Link to="/milista">Mi Lista</Link>
          </nav>

          {user?.role === "admin" && (
            <nav className="admin-nav">
              <Link to="/admin/dashboard">Panel Admin</Link>
              <Link to="/admin/users">Gestionar Usuarios</Link>
            </nav>
          )}

          {user && (
            <div className="user-avatar" onClick={() => setMenuOpen(!menuOpen)}>
              <img src={user.avatar} alt="Avatar" className="avatar-icon" />
              {menuOpen && (
                <div className="dropdown-menu">
                  <Link to="/add-profile">A√±adir perfil</Link>
                  <Link to="/edit-profile">Editar perfil</Link>
                  <Link to={`/usuario/${user.id}`}>Mi perfil</Link>
                  <button onClick={handleLogout}>Cerrar sesi√≥n</button>
                </div>
              )}
            </div>
          )}
        </>
      )}
    </header>
  );
};

export default Header;
