import React, { useState, useEffect } from "react";
import config from "../utils/config";
import "../styles/styles.css";
import Modal from "../components/Modal";
import "font-awesome/css/font-awesome.min.css";

const Index = ({ setFavoriteMessage }) => {
  const [items, setItems] = useState([]); // En este ejemplo, se cargarán películas
  const [selectedItem, setSelectedItem] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Cargar los items (películas)
  useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/movies`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setItems(data);
      } catch (error) {
        console.error("❌ Error al cargar items:", error);
      }
    };
    fetchItems();
  }, []);

  const handleClick = async (itemId, type = "movies") => {
    try {
      const response = await fetch(`${config.API_URL}/api/${type}/${itemId}`);
      const data = await response.json();
      setSelectedItem({ ...data, type });
      setIsModalOpen(true);
    } catch (error) {
      console.error("❌ Error al cargar el detalle:", error);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedItem(null);
  };

  const ContentCard = ({ item }) => {
    const favoriteKey = String(item.Id_Movie);
    const [isFavorite, setIsFavorite] = useState(false);
    const [message, setMessage] = useState("");

    useEffect(() => {
      const userStr = sessionStorage.getItem("user");
      if (userStr) {
        const user = JSON.parse(userStr);
        const favs = user.Favorites ? user.Favorites.split(",").map((f) => f.trim()) : [];
        setIsFavorite(favs.includes(favoriteKey));
      }
    }, [item, favoriteKey]);

    const handleFavoriteClick = async (e) => {
      e.stopPropagation();
      const newFavState = !isFavorite;
      setIsFavorite(newFavState);

      setFavoriteMessage(newFavState ? "Añadido a favoritos" : "Eliminado de favoritos");
      setTimeout(() => setFavoriteMessage(""), 2000);
      setMessage(newFavState ? "Añadido a favoritos" : "Eliminado de favoritos");
      setTimeout(() => setMessage(""), 2000);

      const userStr = sessionStorage.getItem("user");
      if (userStr) {
        const user = JSON.parse(userStr);
        if (!user.Favorites || user.Favorites === "undefined") {
          user.Favorites = "";
        }
        let favs = user.Favorites ? user.Favorites.split(",").map((f) => f.trim()).filter(f => f !== "" && f !== "undefined") : [];
        if (newFavState) {
          if (!favs.includes(favoriteKey)) {
            favs.push(favoriteKey);
          }
        } else {
          favs = favs.filter(f => f !== favoriteKey);
        }
        user.Favorites = favs.join(",");
        sessionStorage.setItem("user", JSON.stringify(user));
      }

      try {
        const userObj = JSON.parse(sessionStorage.getItem("user"));
        const requestBody = {
          userId: userObj.Id_User,
          itemKey: favoriteKey,
          isFavorite: newFavState,
        };

        const response = await fetch(`${config.API_URL}/api/usuario/favorites`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(requestBody),
        });
        if (!response.ok) {
          throw new Error(`Error actualizando favoritos (Código: ${response.status})`);
        }
        const responseData = await response.json();
        console.log("✅ Respuesta del backend:", responseData);

        const updatedUserResponse = await fetch(`${config.API_URL}/api/usuario/${userObj.Id_User}`);
        if (updatedUserResponse.ok) {
          const updatedUserData = await updatedUserResponse.json();
          sessionStorage.setItem("user", JSON.stringify(updatedUserData));
          window.dispatchEvent(new CustomEvent("favoritesUpdated"));
        } else {
          console.error("❌ Error recargando usuario desde el backend");
        }
      } catch (error) {
        console.error("❌ Error actualizando favoritos:", error);
      }
    };

    return (
      <div className="content-card" onClick={() => handleClick(item.Id_Movie, "movies")}>
        <div className="card-image-container" style={{ position: "relative" }}>
          <img src={`${config.API_URL}${item.Image}`} alt={item.Title} />
          <div
            className="favorite-icon"
            onClick={handleFavoriteClick}
            style={{ position: "absolute", top: "10px", left: "10px", cursor: "pointer" }}
          >
            <i className={`fa ${isFavorite ? "fa-star" : "fa-star-o"}`} style={{ fontSize: "24px", color: "gold" }} />
            {message && (
              <div
                style={{
                  position: "absolute",
                  top: "0",
                  left: "40px",
                  background: "rgba(0,0,0,0.7)",
                  padding: "3px 8px",
                  borderRadius: "5px",
                  color: "white",
                  fontSize: "14px",
                }}
              >
                {message}
              </div>
            )}
          </div>
        </div>
        <p>{item.Title}</p>
      </div>
    );
  };

  return (
    <div className="content-page">
      <h1>Index</h1>
      <div className="content-carousel">
        {items.map((item) => (
          <ContentCard key={item.Id_Movie} item={item} />
        ))}
      </div>
      <Modal isOpen={isModalOpen} onClose={closeModal} data={selectedItem} setFavoriteMessage={setFavoriteMessage} />
    </div>
  );
};

export default Index;
