import React, { useState, useEffect } from "react";
import "../styles/modal.css";
import config from "../utils/config";

const Modal = ({ isOpen, onClose, data, setFavoriteMessage }) => {
  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(() => {
    if (data) {
      const favoriteId = data.type === "movies" ? data.Id_Movie : data.Id_Series;
      const userStr = sessionStorage.getItem("user");
      if (userStr) {
        const user = JSON.parse(userStr);
        const favs = user.Favorites ? user.Favorites.split(",").map((fav) => fav.trim()) : [];
        setIsFavorite(favs.includes(String(favoriteId)));
      }
    }
  }, [data]);

  const handleFavoriteClick = async () => {
    if (!data) return;
    const favoriteId = data.type === "movies" ? data.Id_Movie : data.Id_Series;
    const newFavState = !isFavorite;
    setIsFavorite(newFavState);

    console.log("📌 Antes de modificar favoritos:", sessionStorage.getItem("user"));

    // Verificar que setFavoriteMessage es una función antes de ejecutarla
    if (typeof setFavoriteMessage === "function") {
      setFavoriteMessage(newFavState ? "Añadido a favoritos" : "Eliminado de favoritos");
      setTimeout(() => setFavoriteMessage(""), 2000); // Se oculta después de 2 segundos
    } else {
      console.error("⚠ setFavoriteMessage no es una función");
    }

    // Actualizar sessionStorage
    const userStr = sessionStorage.getItem("user");
    if (userStr) {
      const user = JSON.parse(userStr);
      let favs = user.Favorites
        ? user.Favorites.split(",").map((f) => f.trim()).filter((f) => f !== "")
        : [];
      if (newFavState) {
        if (!favs.includes(String(favoriteId))) {
          favs.push(String(favoriteId));
        }
      } else {
        favs = favs.filter((f) => f !== String(favoriteId));
      }
      user.Favorites = favs.join(",");
      sessionStorage.setItem("user", JSON.stringify(user));
      console.log("📌 Después de modificar favoritos:", JSON.parse(sessionStorage.getItem("user")));
    }

    // Enviar la actualización al backend mediante PUT
    try {
      const userObj = JSON.parse(sessionStorage.getItem("user"));
      const requestBody = {
        userId: userObj.id,
        itemId: String(favoriteId),
        isFavorite: newFavState,
      };

      console.log("📤 Enviando datos al backend:", requestBody);

      const response = await fetch(`${config.API_URL}/api/usuario/favorites`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(requestBody),
      });
      if (!response.ok) {
        throw new Error("Error actualizando favoritos");
      }
      await response.json();
    } catch (error) {
      console.error("❌ Error actualizando favoritos desde el modal:", error);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="close-button" onClick={onClose}>X</button>
        <h2>{data?.Title}</h2>

        {/* Botón de Favoritos */}
        <div className="modal-favorite">
          <button onClick={handleFavoriteClick}>
            {isFavorite ? "Eliminar de Favoritos" : "Añadir a Favoritos"}
          </button>
        </div>

        {/* Imagen más pequeña */}
        <img className="modal-image" src={`${config.API_URL}${data?.Image}`} alt={data?.Title} />

        <p><strong>Año de lanzamiento:</strong> {data?.Release_Year}</p>
        {data?.type === "movies" && <p><strong>Duración:</strong> {data?.Duration} min</p>}
        {data?.type === "series" && <p><strong>Temporadas:</strong> {data?.Seasons}</p>}
        <p><strong>Sinopsis:</strong> {data?.Synopsis}</p>

        {/* Sección de enlaces */}
        <div className="modal-links">
          <h3>Enlaces</h3>
          <ul>
            <li><a href="#">Ver en streaming</a></li>
            <li><a href="#">Tráiler oficial</a></li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Modal;
