import React, { useState, useEffect } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import config from "../utils/config";

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation(); // Hook para conocer la ruta actual
  const isAuthPage = location.pathname === "/login" || location.pathname === "/register";
  const [menuOpen, setMenuOpen] = useState(false);
  const [user, setUser] = useState(null);

  // Ahora el efecto se ejecutará cada vez que cambie la ubicación
  useEffect(() => {
    const storedUser = sessionStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    } else {
      setUser(null);
    }
  }, [location]);  // Agregamos "location" para que se actualice al cambiar la ruta

  const handleLogout = () => {
    sessionStorage.removeItem("accessToken");
    sessionStorage.removeItem("user");
    localStorage.removeItem("refreshToken");
    navigate("/login");
  };

  return (
    <header className="header">
      {/* Logotipo y Nombre */}
      <div className="logo-container">
        <Link to="/index" className="logo">
          <img src={`${config.API_URL}/assets/logo.jpg`} alt="Blockbuster Logo" />
          <span>Blockbuster</span>
        </Link>
      </div>

      {/* Si NO es login o register, se muestran el resto de los menús */}
      {!isAuthPage && (
        <>
          <nav className="nav-links">
            <Link to="/series">Series</Link>
            <Link to="/peliculas">Películas</Link>
          </nav>

          {user?.role === "admin" && (
            <nav className="admin-nav">
              <Link to="/admin/dashboard">Panel Admin</Link>
              <Link to="/admin/users">Gestionar Usuarios</Link>
            </nav>
          )}

          {user && (
            <div className="user-avatar" onClick={() => setMenuOpen(!menuOpen)}>
              <img src={user.avatar} alt="Avatar" className="avatar-icon" />
              {menuOpen && (
                <div className="dropdown-menu">
                  <Link to="/add-profile">Añadir perfil</Link>
                  <Link to="/edit-profile">Editar perfil</Link>
                  <Link to={`/usuario/${user.id}`}>Mi perfil</Link>
                  <button onClick={handleLogout}>Cerrar sesión</button>
                </div>
              )}
            </div>
          )}
        </>
      )}
    </header>
  );
};

export default Header;
