// Movies.jsx
import React, { useState, useEffect } from "react";
import config from "../utils/config";
import "../styles/styles.css";
import Modal from "../components/Modal";
import 'font-awesome/css/font-awesome.min.css';

const Movies = () => {
  const [moviesList, setMoviesList] = useState([]); // Estado para almacenar películas
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Obtener películas desde la API
  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/movies`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setMoviesList(data);
      } catch (error) {
        console.error("❌ Error al cargar las películas:", error);
      }
    };

    fetchMovies();
  }, []);

  // Función para mostrar el modal con detalles de la película
  const handleClick = async (movieId) => {
    try {
      const response = await fetch(`${config.API_URL}/api/movies/${movieId}`);
      const data = await response.json();
      setSelectedMovie({ ...data, type: "movies" });
      setIsModalOpen(true);
    } catch (error) {
      console.error("❌ Error al cargar la película:", error);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedMovie(null);
  };

  // Subcomponente para representar cada tarjeta de película con funcionalidad de favorito
  const ContentCard = ({ item }) => {
    const [isFavorite, setIsFavorite] = useState(false);
    const [message, setMessage] = useState("");

    // Determinar el estado inicial del favorito
    useEffect(() => {
      const userStr = sessionStorage.getItem("user");
      if (userStr) {
        const user = JSON.parse(userStr);
        if (user.Favorites) {
          const favs = user.Favorites.split(",");
          setIsFavorite(favs.includes(String(item.Id_Movie)));
        }
      }
    }, [item]);

    const handleFavoriteClick = async (e) => {
      e.stopPropagation(); // Prevenir que se active el clic en el contenedor principal
      const newFavState = !isFavorite;
      setIsFavorite(newFavState);
      setMessage(newFavState ? "Añadido a favoritos" : "Eliminado de favoritos");
      setTimeout(() => setMessage(""), 2000);

      const userStr = sessionStorage.getItem("user");
      if (userStr) {
        const user = JSON.parse(userStr);
        try {
          const response = await fetch(`${config.API_URL}/api/usuario/favorites`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              userId: user.id,
              itemId: item.Id_Movie,
              isFavorite: newFavState,
            }),
          });
          if (!response.ok) {
            throw new Error("Error actualizando favoritos");
          }
          const data = await response.json();
        } catch (error) {
          console.error("❌ Error actualizando favoritos:", error);
        }
      }
    };

    return (
      <div className="content-card" onClick={() => handleClick(item.Id_Movie)}>
        <div className="card-image-container" style={{ position: "relative" }}>
          <img src={`${config.API_URL}${item.Image}`} alt={item.Title} />
          <div
            className="favorite-icon"
            onClick={handleFavoriteClick}
            style={{
              position: "absolute",
              top: "10px",
              left: "10px",
              cursor: "pointer",
            }}
          >
            <i
              className={`fa ${isFavorite ? "fa-star" : "fa-star-o"}`}
              style={{ fontSize: "24px", color: "gold" }}
            />
            {message && (
              <div
                className="favorite-message"
                style={{
                  position: "absolute",
                  top: "0",
                  left: "40px",
                  background: "rgba(0,0,0,0.7)",
                  padding: "3px 8px",
                  borderRadius: "5px",
                  color: "white",
                  fontSize: "14px",
                }}
              >
                {message}
              </div>
            )}
          </div>
        </div>
        <p>{item.Title}</p>
      </div>
    );
  };

  return (
    <div className="content-page">
      <h1>Películas</h1>
      <div className="content-carousel">
        {moviesList.map((movie) => (
          <ContentCard key={movie.Id_Movie} item={movie} />
        ))}
      </div>

      <Modal isOpen={isModalOpen} onClose={closeModal} data={selectedMovie} />
    </div>
  );
};

export default Movies;
