import React, { useState, useEffect } from "react";
import config from "../utils/config";
import "../styles/styles.css";
import Modal from "../components/Modal";
import "font-awesome/css/font-awesome.min.css";

const Series = ({ setFavoriteMessage }) => {
  const [seriesList, setSeriesList] = useState([]); // Arreglo de series
  const [selectedSeries, setSelectedSeries] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Cargar la lista de series desde la API
  useEffect(() => {
    const fetchSeries = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/series`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setSeriesList(data);
      } catch (error) {
        console.error("❌ Error al cargar las series:", error);
      }
    };
    fetchSeries();
  }, []);

  // Abre el modal con detalle de la serie
  const handleClick = async (seriesId) => {
    try {
      const response = await fetch(`${config.API_URL}/api/series/${seriesId}`);
      const data = await response.json();
      setSelectedSeries({ ...data, type: "series" });
      setIsModalOpen(true);
    } catch (error) {
      console.error("❌ Error al cargar la serie:", error);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedSeries(null);
  };

  // Componente para cada tarjeta de serie
  const ContentCard = ({ item }) => {
    const favoriteKey = String(item.Id_Series);
    const [isFavorite, setIsFavorite] = useState(false);
    const [message, setMessage] = useState("");

    useEffect(() => {
      const userStr = sessionStorage.getItem("user");
      if (userStr) {
        const user = JSON.parse(userStr);
        const favs = user.Favorites ? user.Favorites.split(",").map((f) => f.trim()) : [];
        setIsFavorite(favs.includes(favoriteKey));
      }
    }, [item, favoriteKey]);

    const handleFavoriteClick = async (e) => {
      e.stopPropagation();
      const newFavState = !isFavorite;
      setIsFavorite(newFavState);

      console.log("📌 Antes de modificar favoritos:", sessionStorage.getItem("user"));

      // Establecer mensajes (global y local)
      setFavoriteMessage(newFavState ? "Añadido a favoritos" : "Eliminado de favoritos");
      setTimeout(() => setFavoriteMessage(""), 2000);
      setMessage(newFavState ? "Añadido a favoritos" : "Eliminado de favoritos");
      setTimeout(() => setMessage(""), 2000);

      // Actualizar sessionStorage (limpiando posibles "undefined" en Favorites)
      const userStr = sessionStorage.getItem("user");
      if (userStr) {
        const user = JSON.parse(userStr);
        if (!user.Favorites || user.Favorites === "undefined") {
          user.Favorites = "";
        }
        let favs = user.Favorites
          ? user.Favorites.split(",").map((f) => f.trim()).filter(f => f !== "" && f !== "undefined")
          : [];
        if (newFavState) {
          if (!favs.includes(favoriteKey)) {
            favs.push(favoriteKey);
          }
        } else {
          favs = favs.filter(f => f !== favoriteKey);
        }
        user.Favorites = favs.join(",");
        sessionStorage.setItem("user", JSON.stringify(user));
        console.log("📌 Después de modificar favoritos:", JSON.parse(sessionStorage.getItem("user")));
      }

      // Enviar actualización al backend (usando Id_User y enviando itemKey)
      try {
        const userObj = JSON.parse(sessionStorage.getItem("user"));
        const requestBody = {
          userId: userObj.Id_User, // propiedad correcta
          itemKey: favoriteKey,    // se espera itemKey en el endpoint
          isFavorite: newFavState,
        };

        console.log("📤 Datos enviados al backend:", requestBody);

        const response = await fetch(`${config.API_URL}/api/usuario/favorites`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(requestBody),
        });

        if (!response.ok) {
          throw new Error(`Error actualizando favoritos (Código: ${response.status})`);
        }
        const responseData = await response.json();
        console.log("✅ Respuesta del backend:", responseData);

        // Recargar usuario desde el backend para sincronizar sessionStorage
        const updatedUserResponse = await fetch(`${config.API_URL}/api/usuario/${userObj.Id_User}`);
        if (updatedUserResponse.ok) {
          const updatedUserData = await updatedUserResponse.json();
          sessionStorage.setItem("user", JSON.stringify(updatedUserData));
          console.log("🔄 sessionStorage actualizado después del PUT:", updatedUserData);
          window.dispatchEvent(new CustomEvent("favoritesUpdated"));
        } else {
          console.error("❌ Error recargando usuario desde el backend");
        }
      } catch (error) {
        console.error("❌ Error actualizando favoritos:", error);
      }
    };

    return (
      <div className="content-card" onClick={() => handleClick(item.Id_Series)}>
        <div className="card-image-container" style={{ position: "relative" }}>
          <img src={`${config.API_URL}${item.Image}`} alt={item.Title} />
          <div
            className="favorite-icon"
            onClick={handleFavoriteClick}
            style={{
              position: "absolute",
              top: "10px",
              left: "10px",
              cursor: "pointer",
            }}
          >
            <i
              className={`fa ${isFavorite ? "fa-star" : "fa-star-o"}`}
              style={{ fontSize: "24px", color: "gold" }}
            />
            {message && (
              <div
                style={{
                  position: "absolute",
                  top: "0",
                  left: "40px",
                  background: "rgba(0,0,0,0.7)",
                  padding: "3px 8px",
                  borderRadius: "5px",
                  color: "white",
                  fontSize: "14px",
                }}
              >
                {message}
              </div>
            )}
          </div>
        </div>
        <p>{item.Title}</p>
      </div>
    );
  };

  return (
    <div className="content-page">
      <h1>Series</h1>
      <div className="content-carousel">
        {seriesList.map((series) => (
          <ContentCard key={series.Id_Series} item={series} />
        ))}
      </div>
      <Modal
        isOpen={isModalOpen}
        onClose={closeModal}
        data={selectedSeries}
        setFavoriteMessage={setFavoriteMessage}
      />
    </div>
  );
};

export default Series;
