import React, { useEffect, useState } from "react";
import config from "../utils/config";
import "../styles/styles.css";

const AdminPanel = () => {
    const [movies, setMovies] = useState([]);
    const [series, setSeries] = useState([]);
    const [genres, setGenres] = useState([]); 
    const [searchQuery, setSearchQuery] = useState("");
    const [selectedItem, setSelectedItem] = useState(null);
    const [editData, setEditData] = useState({});
    const [error, setError] = useState("");
    const [isAdding, setIsAdding] = useState(false);
    const [newItem, setNewItem] = useState({ title: "", genreId: "", releaseYear: "", duration: "", language: "", type: "movies" });

    useEffect(() => {
        const fetchData = async () => {
            const accessToken = sessionStorage.getItem("accessToken");

            if (!accessToken) {
                setError("❌ No hay token de autenticación.");
                return;
            }

            try {
                const [moviesResponse, seriesResponse, genresResponse] = await Promise.all([
                    fetch(`${config.API_URL}/api/admin/movies`, {
                        method: "GET",
                        headers: { "Authorization": `Bearer ${accessToken}`, "Content-Type": "application/json" }
                    }),
                    fetch(`${config.API_URL}/api/admin/series`, {
                        method: "GET",
                        headers: { "Authorization": `Bearer ${accessToken}`, "Content-Type": "application/json" }
                    }),
                    fetch(`${config.API_URL}/api/admin/genres`, {
                        method: "GET",
                        headers: { "Authorization": `Bearer ${accessToken}`, "Content-Type": "application/json" }
                    })
                ]);

                if (!moviesResponse.ok || !seriesResponse.ok || !genresResponse.ok) {
                    throw new Error("Error al obtener datos.");
                }

                setMovies(await moviesResponse.json());
                setSeries(await seriesResponse.json());
                setGenres(await genresResponse.json());
            } catch (error) {
                setError(error.message);
            }
        };

        fetchData();
    }, []);

    const handleCreate = async () => {
        console.log("📌 handleCreate activado con:", newItem);
        const accessToken = sessionStorage.getItem("accessToken");

        if (!accessToken) {
            setError("❌ No hay token de autenticación.");
            return;
        }

        try {
            const response = await fetch(`${config.API_URL}/api/admin/${newItem.type}`, {
                method: "POST",
                headers: { 
                    "Authorization": `Bearer ${accessToken}`,
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(newItem) 
            });

            console.log("📌 Solicitud enviada al backend:", JSON.stringify(newItem));

            if (!response.ok) {
                throw new Error("Error al crear contenido.");
            }

            const createdItem = await response.json();

            if (newItem.type === "movies") {
                setMovies([...movies, createdItem]);
            } else {
                setSeries([...series, createdItem]);
            }

            setIsAdding(false);
        } catch (error) {
            console.error("❌ Error al crear contenido:", error);
            setError(error.message);
        }
    };  

    const handleDelete = async (type, id) => {
        console.log(`📌 Eliminando ${type} con ID: ${id}`);
        const accessToken = sessionStorage.getItem("accessToken");

        try {
            const response = await fetch(`${config.API_URL}/api/admin/${type}/${id}`, {
                method: "DELETE",
                headers: { "Authorization": `Bearer ${accessToken}` }
            });

            if (!response.ok) {
                throw new Error("Error al eliminar contenido.");
            }

            if (type === "movies") {
                setMovies(movies.filter(movie => movie.id !== id));
            } else {
                setSeries(series.filter(serie => serie.id !== id));
            }

            console.log(`✅ ${type} eliminado correctamente.`);
        } catch (error) {
            console.error("❌ Error al eliminar contenido:", error);
            setError(error.message);
        }
    };

    return (
        <div className="admin-container">
            <h1>Panel de Administración</h1>
            {error && <p className="error-message">{error}</p>}

            <input 
                type="text" 
                placeholder="Buscar título..." 
                onChange={(e) => setSearchQuery(e.target.value.toLowerCase())} 
                className="search-box"
            />

            <h2>Películas</h2>
            <button className="add-btn" onClick={() => { 
                console.log("📌 Botón Añadir presionado"); 
                setIsAdding(true); 
                setNewItem({ title: "", genreId: "", releaseYear: "", duration: "", language: "", type: "movies" }); 
            }}>
                Añadir Película
            </button>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Título</th>
                        <th>Género</th>
                        <th>Año</th>
                        <th>Duración</th>
                        <th>Idioma</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {movies.filter(movie => movie?.Title?.toLowerCase().includes(searchQuery || "")).map(movie => (
                        <tr key={movie.id}>
                            <td>{movie.id}</td>
                            <td>{movie.Title}</td>
                            <td>{movie.Genre}</td>
                            <td>{movie.Release_Year}</td>
                            <td>{movie.Duration} min</td>
                            <td>{movie.Language}</td>
                            <td>
                                <button className="delete-btn" onClick={() => handleDelete("movies", movie.id)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h2>Series</h2>
            <button className="add-btn" onClick={() => { 
                console.log("📌 Botón Añadir Serie presionado"); 
                setIsAdding(true); 
                setNewItem({ title: "", genreId: "", releaseYear: "", seasons: "", language: "", type: "series" }); 
            }}>
                Añadir Serie
            </button>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Título</th>
                        <th>Género</th>
                        <th>Año</th>
                        <th>Temporadas</th>
                        <th>Idioma</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {series.filter(serie => serie?.Title?.toLowerCase().includes(searchQuery || "")).map(serie => (
                        <tr key={serie.id}>
                            <td>{serie.id}</td>
                            <td>{serie.Title}</td>
                            <td>{serie.Genre}</td>
                            <td>{serie.Release_Year}</td>
                            <td>{serie.Seasons}</td>
                            <td>{serie.Language}</td>
                            <td>
                                <button className="delete-btn" onClick={() => handleDelete("series", serie.id)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AdminPanel;
