import React, { useEffect, useState } from "react";
import config from "../utils/config";
import "../styles/styles.css";

const AdminPanel = () => {
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);
  const [genres, setGenres] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [error, setError] = useState("");
  const [isAdding, setIsAdding] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editItem, setEditItem] = useState(null);

  // Objeto para la creaci√≥n
  const [newItem, setNewItem] = useState({
    Title: "",
    genreId: "",
    releaseYear: "",
    duration: "", // Para pel√≠culas
    seasons: "", // Para series
    language: "",
    type: "movies", // "movies" o "series"
  });

  useEffect(() => {
    const fetchData = async () => {
      const accessToken = sessionStorage.getItem("accessToken");
      if (!accessToken) {
        setError("‚ùå No hay token de autenticaci√≥n.");
        return;
      }
      try {
        const [moviesResponse, seriesResponse, genresResponse] = await Promise.all([
          fetch(`${config.API_URL}/api/admin/movies`, {
            method: "GET",
            headers: {
              "Authorization": `Bearer ${accessToken}`,
              "Content-Type": "application/json",
            },
          }),
          fetch(`${config.API_URL}/api/admin/series`, {
            method: "GET",
            headers: {
              "Authorization": `Bearer ${accessToken}`,
              "Content-Type": "application/json",
            },
          }),
          fetch(`${config.API_URL}/api/admin/genres`, {
            method: "GET",
            headers: {
              "Authorization": `Bearer ${accessToken}`,
              "Content-Type": "application/json",
            },
          }),
        ]);

        if (!moviesResponse.ok || !seriesResponse.ok || !genresResponse.ok) {
          throw new Error("Error al obtener datos.");
        }

        const moviesData = await moviesResponse.json();
        const seriesData = await seriesResponse.json();
        const genresData = await genresResponse.json();

        console.log("üìå Pel√≠culas recibidas:", JSON.stringify(moviesData, null, 2));
        console.log("üìå Series recibidas:", JSON.stringify(seriesData, null, 2));
        console.log("üìå G√©neros recibidos:", JSON.stringify(genresData, null, 2));

        setMovies(moviesData);
        setSeries(seriesData);
        setGenres(genresData);
      } catch (error) {
        setError(error.message);
      }
    };

    fetchData();
  }, []);

  // Funci√≥n para crear un elemento (pel√≠cula o serie)
  const handleCreate = async () => {
    const accessToken = sessionStorage.getItem("accessToken");
    if (!accessToken) {
      setError("‚ùå No hay token de autenticaci√≥n.");
      return;
    }
    try {
      const response = await fetch(`${config.API_URL}/api/admin/${newItem.type}`, {
        method: "POST",
        headers: {
          "Authorization": `Bearer ${accessToken}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          Title: newItem.Title,
          genreId: newItem.genreId,
          releaseYear: parseInt(newItem.releaseYear, 10),
          duration: newItem.type === "movies" ? parseInt(newItem.duration, 10) : undefined,
          seasons: newItem.type === "series" ? parseInt(newItem.seasons, 10) : undefined,
          language: newItem.language,
        }),
      });
      if (!response.ok) {
        throw new Error("Error al crear contenido.");
      }
      const createdItem = await response.json();
      if (newItem.type === "movies") {
        setMovies([...movies, createdItem]);
      } else {
        setSeries([...series, createdItem]);
      }
      setIsAdding(false);
    } catch (err) {
      setError(err.message);
    }
  };

  // Funci√≥n para eliminar contenido
  const handleDelete = async (type, id) => {
    const accessToken = sessionStorage.getItem("accessToken");
    try {
      const response = await fetch(`${config.API_URL}/api/admin/${type}/${id}`, {
        method: "DELETE",
        headers: { "Authorization": `Bearer ${accessToken}` },
      });
      if (!response.ok) {
        throw new Error("Error al eliminar contenido.");
      }
      if (type === "movies") {
        setMovies(movies.filter((movie) => movie.Id_Movie !== id && movie.id !== id));
      } else {
        setSeries(series.filter((serie) => serie.Id_Series !== id && serie.id !== id));
      }
    } catch (err) {
      setError(err.message);
    }
  };

  // Funci√≥n para iniciar la edici√≥n
  const handleEdit = (item) => {
    // Si se defini√≥ "type" al invocar la funci√≥n, se usar√°; de lo contrario, se intenta inferir
    const resolvedType = item.type;
    // Se busca el ID: si es pel√≠cula, usamos item.id o item.Id_Movie; para series, item.id o item.Id_Series
    const id =
      resolvedType === "movies" ? (item.id || item.Id_Movie) : (item.id || item.Id_Series);
    setEditItem({ ...item, id, type: resolvedType });
    setIsEditing(true);
  };  

  // Captura de cambios en el modal de edici√≥n
  const handleEditInputChange = (e) => {
    const { name, value } = e.target;
    setEditItem({ ...editItem, [name]: value });
  };

  // Funci√≥n para actualizar en el backend
  const handleUpdate = async () => {
    const accessToken = sessionStorage.getItem("accessToken");
  
    const updatedData = {
      Title: editItem.Title,
      genreId: parseInt(editItem.genreId, 10),
      releaseYear: parseInt(editItem.releaseYear, 10),
      duration: editItem.type === "movies" ? parseInt(editItem.duration, 10) : undefined,
      seasons: editItem.type === "series" ? parseInt(editItem.seasons, 10) : undefined,
      language: editItem.language,
    };
  
    console.log("üìå Datos enviados al backend:", updatedData);
  
    try {
      const response = await fetch(
        `${config.API_URL}/api/admin/${editItem.type}/${editItem.id}`,
        {
          method: "PUT",
          headers: { 
            "Authorization": `Bearer ${accessToken}`,
            "Content-Type": "application/json"
          },
          body: JSON.stringify(updatedData)
        }
      );
  
      if (!response.ok) {
        throw new Error("Error al actualizar contenido.");
      }
  
      // Se obtiene el objeto actualizado del backend
      const updatedItem = await response.json();
  
      // Actualizamos el estado local reemplazando el elemento actualizado
      if (editItem.type === "movies") {
        setMovies(prev =>
          prev.map(movie =>
            (movie.id === editItem.id || movie.Id_Movie === editItem.id)
              ? updatedItem
              : movie
          )
        );
      } else if (editItem.type === "series") {
        setSeries(prev =>
          prev.map(serie =>
            (serie.id === editItem.id || serie.Id_Series === editItem.id)
              ? updatedItem
              : serie
          )
        );
      }
      
      setIsEditing(false);
    } catch (error) {
      console.error("‚ùå Error en actualizaci√≥n:", error);
      setError(error.message);
    }
  };  

  return (
    <div className="admin-container">
      <h1>Panel de Administraci√≥n</h1>
      {error && <p className="error-message">{error}</p>}
      <input
        type="text"
        placeholder="Buscar t√≠tulo..."
        onChange={(e) => setSearchQuery(e.target.value.toLowerCase())}
        className="search-box"
      />

      {/* Secci√≥n de Pel√≠culas */}
      <h2>Pel√≠culas</h2>
      <button
        className="add-btn"
        onClick={() => {
          setIsAdding(true);
          setNewItem({
            Title: "",
            genreId: "",
            releaseYear: "",
            duration: "",
            language: "",
            type: "movies",
          });
        }}
      >
        A√±adir Pel√≠cula
      </button>
      <table className="users-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>T√≠tulo</th>
            <th>G√©nero</th>
            <th>A√±o</th>
            <th>Duraci√≥n</th>
            <th>Idioma</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {movies
            .filter(
              (movie) =>
                movie.Title &&
                movie.Title.toLowerCase().includes(searchQuery)
            )
            .map((movie) => (
              <tr key={movie.id || movie.Id_Movie}>
                <td>{movie.id || movie.Id_Movie}</td>
                <td>{movie.Title}</td>
                <td>
                  {genres?.length > 0
                    ? genres.find(
                        (g) => Number(g.Id_Genre) === Number(movie.Genre)
                      )?.Name || "Sin g√©nero"
                    : "Cargando..."}
                </td>
                <td>{movie.Release_Year ? movie.Release_Year : "N/A"}</td>
                <td>{movie.Duration ? `${movie.Duration} min` : "N/A"}</td>
                <td>{movie.Language ? movie.Language : "No definido"}</td>
                <td>
                <button onClick={() => handleEdit({ ...movie, type: "movies" })}>Modificar</button>
                <button onClick={() => handleDelete("movies", movie.id || movie.Id_Movie)}>
                    Eliminar
                </button>
                </td>
              </tr>
            ))}
        </tbody>
      </table>

      {/* Secci√≥n de Series */}
      <h2>Series</h2>
      <button
        className="add-btn"
        onClick={() => {
          setIsAdding(true);
          setNewItem({
            Title: "",
            genreId: "",
            releaseYear: "",
            seasons: "",
            language: "",
            type: "series",
          });
        }}
      >
        A√±adir Serie
      </button>
      <table className="users-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>T√≠tulo</th>
            <th>G√©nero</th>
            <th>A√±o</th>
            <th>Temporadas</th>
            <th>Idioma</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {series
            .filter(
              (serie) =>
                serie.Title &&
                serie.Title.toLowerCase().includes(searchQuery)
            )
            .map((serie) => (
              <tr key={serie.id || serie.Id_Series}>
                <td>{serie.id || serie.Id_Series}</td>
                <td>{serie.Title}</td>
                <td>
                  {genres?.length > 0
                    ? genres.find(
                        (g) => Number(g.Id_Genre) === Number(serie.Genre)
                      )?.Name || "Sin g√©nero"
                    : "Cargando..."}
                </td>
                <td>{serie.Release_Year ? serie.Release_Year : "N/A"}</td>
                <td>{serie.Seasons ? serie.Seasons : "N/A"}</td>
                <td>{serie.Language ? serie.Language : "No definido"}</td>
                <td>
                <button onClick={() => handleEdit({ ...serie, type: "series" })}>Modificar</button>

                    <button onClick={() => handleDelete("series", serie.id || serie.Id_Series)}>
                        Eliminar
                    </button>
                </td>
              </tr>
            ))}
        </tbody>
      </table>

      {/* Modal de Edici√≥n / Modificar */}
      {isEditing && editItem && (
        <div className="modal">
          <div className="modal-content">
            <h2>
              Editar {editItem.type === "movies" ? "Pel√≠cula" : "Serie"}
            </h2>
            <label>T√≠tulo:</label>
            <input
              type="text"
              name="Title"
              value={editItem.Title || ""}
              onChange={handleEditInputChange}
            />

            <label>G√©nero:</label>
            <select
              name="genreId"
              value={editItem.genreId || ""}
              onChange={handleEditInputChange}
            >
              <option value="">Seleccionar g√©nero...</option>
              {genres.map((genre) => (
                <option key={genre.Id_Genre} value={genre.Id_Genre}>
                  {genre.Name}
                </option>
              ))}
            </select>

            <label>A√±o de estreno:</label>
            <input
              type="number"
              name="releaseYear"
              value={editItem.releaseYear || ""}
              onChange={handleEditInputChange}
            />

            {editItem.type === "movies" ? (
              <>
                <label>Duraci√≥n (min):</label>
                <input
                  type="number"
                  name="duration"
                  value={editItem.duration || ""}
                  onChange={handleEditInputChange}
                />
              </>
            ) : (
              <>
                <label>Temporadas:</label>
                <input
                  type="number"
                  name="seasons"
                  value={editItem.seasons || ""}
                  onChange={handleEditInputChange}
                />
              </>
            )}

            <label>Idioma:</label>
            <input
              type="text"
              name="language"
              value={editItem.language || ""}
              onChange={handleEditInputChange}
            />

            <button className="update-btn" onClick={handleUpdate}>
              Guardar Cambios
            </button>
            <button className="cancel-btn" onClick={() => setIsEditing(false)}>
              Cancelar
            </button>
          </div>
        </div>
      )}

      {/* Modal de Creaci√≥n */}
      {isAdding && (
        <div className="modal">
          <div className="modal-content">
            <h2>
              A√±adir {newItem.type === "movies" ? "Pel√≠cula" : "Serie"}
            </h2>
            <input
              type="text"
              name="Title"
              value={newItem.Title}
              onChange={(e) => setNewItem({ ...newItem, Title: e.target.value })}
              placeholder="T√≠tulo"
            />
            <select
              name="genreId"
              value={newItem.genreId}
              onChange={(e) =>
                setNewItem({ ...newItem, genreId: e.target.value })
              }
            >
              <option value="">Seleccionar g√©nero...</option>
              {genres.map((genre) => (
                <option key={genre.Id_Genre} value={genre.Id_Genre}>
                  {genre.Name}
                </option>
              ))}
            </select>
            <input
              type="number"
              name="releaseYear"
              value={newItem.releaseYear}
              onChange={(e) =>
                setNewItem({ ...newItem, releaseYear: e.target.value })
              }
              placeholder="A√±o de estreno"
            />
            {newItem.type === "movies" ? (
              <input
                type="number"
                name="duration"
                value={newItem.duration}
                onChange={(e) =>
                  setNewItem({ ...newItem, duration: e.target.value })
                }
                placeholder="Duraci√≥n en minutos"
              />
            ) : (
              <input
                type="number"
                name="seasons"
                value={newItem.seasons}
                onChange={(e) =>
                  setNewItem({ ...newItem, seasons: e.target.value })
                }
                placeholder="N√∫mero de temporadas"
              />
            )}
            <input
              type="text"
              name="language"
              value={newItem.language}
              onChange={(e) =>
                setNewItem({ ...newItem, language: e.target.value })
              }
              placeholder="Idioma"
            />
            <button onClick={handleCreate}>Guardar</button>
            <button onClick={() => setIsAdding(false)}>Cancelar</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
