import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import config from "../utils/config";
import "../styles/styles.css";

const Register = () => {
  const [user, setUser] = useState({
    name: "",
    email: "",
    address: "",
    password: "",
    confirmPassword: "",
    avatar: "", 
  });

  const [avatars, setAvatars] = useState([]);
  const [errorMessage, setErrorMessage] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // ğŸ”¹ Obtener avatares desde la base de datos
    fetch(`${config.API_URL}/avatars`)
      .then(response => response.json())
      .then(data => setAvatars(data))
      .catch(error => console.error("Error al obtener avatares:", error));
  }, []);

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleAvatarSelect = (avatar) => {
    setUser({ ...user, avatar });
  };

  const validatePassword = (password) => {
    const regex = /^(?=.*[A-Z])(?=.*[a-z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    return regex.test(password);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage("");

    if (user.password !== user.confirmPassword) {
      setErrorMessage("Las contraseÃ±as no coinciden.");
      return;
    }

    if (!validatePassword(user.password)) {
      setErrorMessage("La contraseÃ±a debe tener al menos 1 mayÃºscula, 1 minÃºscula, 1 nÃºmero y 1 carÃ¡cter especial.");
      return;
    }

    if (!user.avatar) {
      setErrorMessage("Debes seleccionar un avatar.");
      return;
    }

    try {
      const response = await fetch(`${config.API_URL}/register`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(user),
      });

      if (!response.ok) {
        const { message } = await response.json();
        throw new Error(message || "Error al registrar. IntÃ©ntalo de nuevo.");
      }

      alert("Registro exitoso.");
      navigate("/login", { replace: true });
    } catch (error) {
      setErrorMessage(error.message);
    }
  };

  return (
    <div className="form-container">
      <div className="form-box">
        <h2>Registro</h2>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <form onSubmit={handleSubmit}>
          <input type="text" name="name" placeholder="Nombre" onChange={handleChange} required />
          <input type="email" name="email" placeholder="Correo" onChange={handleChange} required />
          <input type="text" name="address" placeholder="DirecciÃ³n" onChange={handleChange} required />
          <div className="password-container">
            <input type={showPassword ? "text" : "password"} name="password" placeholder="ContraseÃ±a" onChange={handleChange} required />
          </div>
          <div className="password-container">
            <input type="password" name="confirmPassword" placeholder="Repetir ContraseÃ±a" onChange={handleChange} required />
          </div>
          <button type="button" className="toggle-password" onClick={() => setShowPassword(!showPassword)}>
            {showPassword ? "Ocultar" : "Mostrar"}
          </button>

          {/* ğŸ”¹ SecciÃ³n de selecciÃ³n de avatar */}
          <h3>Selecciona tu avatar:</h3>
          <div className="avatar-selection">
            {avatars.map((avatar, index) => (
              <img
                key={index}
                src={avatar.url} // ğŸ”¹ Usa la URL de la imagen
                alt={`Avatar ${index}`}
                className={`avatar ${user.avatar === avatar.url ? "selected" : ""}`}
                onClick={() => handleAvatarSelect(avatar.url)}
              />
            ))}
          </div>

          <button type="submit">Registrar</button>
        </form>
        <p>Â¿Ya tienes cuenta? <a href="/login">Inicia sesiÃ³n aquÃ­</a></p>
      </div>
    </div>
  );
};

export default Register;
