import React, { useState, useEffect } from "react";
import "../styles/modal.css";
import config from "../utils/config";

// Funci√≥n auxiliar para obtener el usuario desde sessionStorage de forma segura
const getUserFromStorage = () => {
  const userStr = sessionStorage.getItem("user");
  try {
    return userStr ? JSON.parse(userStr) : null;
  } catch (e) {
    console.error("Error al parsear el usuario desde sessionStorage:", e);
    return null;
  }
};

const Modal = ({ isOpen, onClose, data, setFavoriteMessage }) => {
  const [isFavorite, setIsFavorite] = useState(false);
  // Estado para la puntuaci√≥n seleccionada (inicia en null)
  const [userRating, setUserRating] = useState(null);
  // Estado para almacenar la puntuaci√≥n agregada (promedio)
  const [aggregatedRating, setAggregatedRating] = useState(null);

  useEffect(() => {
    if (data) {
      // Determinar el identificador del contenido seg√∫n el tipo
      const favoriteId = data.type === "movies" ? data.Id_Movie : data.Id_Series;
      const user = getUserFromStorage();
      if (user) {
        const favs = user.Favorites
          ? user.Favorites.split(",").map(fav => fav.trim())
          : [];
        setIsFavorite(favs.includes(String(favoriteId)));
      }
      // Consultar el promedio de valoraciones y reiniciar el rating seleccionado
      fetchAggregatedRating();
      setUserRating(null);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [data]);

  // Consulta el promedio de las valoraciones del contenido
  const fetchAggregatedRating = async () => {
    if (!data) return;
    const itemKey = data.type === "movies" ? data.Id_Movie : data.Id_Series;
    try {
      const response = await fetch(`${config.API_URL}/api/contenido/${itemKey}/ratings`);
      if (response.ok) {
        const ratingData = await response.json();
        // Se espera que el backend devuelva { averageRating, totalVotes }
        setAggregatedRating(ratingData.averageRating);
      } else {
        console.error("‚ùå Error fetching aggregated rating, status:", response.status);
      }
    } catch (error) {
      console.error("‚ùå Error fetching aggregated rating:", error);
    }
  };

  // Maneja la selecci√≥n de un rating mediante los botones
  const handleRateSelect = async (value) => {
    setUserRating(value);
    if (!data) return;
    const itemKey = data.type === "movies" ? data.Id_Movie : data.Id_Series;
    const user = getUserFromStorage();
    if (!user) return;
    const requestBody = {
      userId: user.Id_User,       // Se asocia a la columna "user_id"
      itemKey: String(itemKey),     // Se asocia a "item_key"
      rating: value,
    };

    console.log("üì§ Enviando rating al backend:", requestBody);
    try {
      const response = await fetch(`${config.API_URL}/api/usuario/rating`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(requestBody),
      });
      if (!response.ok) {
        throw new Error(`Error actualizando rating (C√≥digo: ${response.status})`);
      }
      const responseData = await response.json();
      console.log("‚úÖ Respuesta del backend (rating):", responseData);
      fetchAggregatedRating();
    } catch (error) {
      console.error("‚ùå Error actualizando rating desde el modal:", error);
    }
  };

  const handleFavoriteClick = async () => {
    if (!data) return;
    const favoriteId = data.type === "movies" ? data.Id_Movie : data.Id_Series;
    const newFavState = !isFavorite;
    setIsFavorite(newFavState);

    console.log("üìå Antes de modificar favoritos:", sessionStorage.getItem("user"));
    if (typeof setFavoriteMessage === "function") {
      setFavoriteMessage(newFavState ? "A√±adido a favoritos" : "Eliminado de favoritos");
      setTimeout(() => setFavoriteMessage(""), 2000);
    } else {
      console.error("‚ö† setFavoriteMessage no es una funci√≥n");
    }

    // Actualizar el objeto usuario en sessionStorage
    const user = getUserFromStorage();
    if (user) {
      let favs = user.Favorites
        ? user.Favorites.split(",").map(f => f.trim()).filter(f => f !== "")
        : [];
      if (newFavState) {
        if (!favs.includes(String(favoriteId))) {
          favs.push(String(favoriteId));
        }
      } else {
        favs = favs.filter(f => f !== String(favoriteId));
      }
      user.Favorites = favs.join(",");
      sessionStorage.setItem("user", JSON.stringify(user));
      console.log("üìå Despu√©s de modificar favoritos:", user);
    }

    try {
      const userObj = getUserFromStorage();
      if (!userObj) return;
      const requestBody = {
        userId: userObj.Id_User,
        itemId: String(favoriteId),
        isFavorite: newFavState,
      };

      console.log("üì§ Enviando datos al backend (favoritos):", requestBody);
      const response = await fetch(`${config.API_URL}/api/usuario/favorites`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(requestBody),
      });
      if (!response.ok) {
        throw new Error("Error actualizando favoritos");
      }
      await response.json();
    } catch (error) {
      console.error("‚ùå Error actualizando favoritos desde el modal:", error);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        <button className="close-button" onClick={onClose}>X</button>
        <h2>{data?.Title}</h2>

        {/* Bot√≥n de Favoritos */}
        <div className="modal-favorite">
          <button onClick={handleFavoriteClick}>
            {isFavorite ? "Eliminar de Favoritos" : "A√±adir a Favoritos"}
          </button>
        </div>

        {/* Secci√≥n de Rating con botones */}
        <div className="modal-rating">
          <h3>Califica este contenido</h3>
          <div className="rating-buttons">
            {Array.from({ length: 10 }, (_, i) => i + 1).map(value => (
              <button
                key={value}
                onClick={() => handleRateSelect(value)}
                className={`rating-button ${userRating === value ? "selected" : ""}`}
              >
                {value}
              </button>
            ))}
          </div>
          <div className="rating-display">
            <span>Tu puntuacion: {userRating !== null ? userRating : "No seleccionada"}</span>
          </div>
          {aggregatedRating !== null && (
            <p className="aggregated-rating">
              Puntuaci√≥n promedio: {aggregatedRating.toFixed(1)}
            </p>
          )}
        </div>

        {/* Imagen (miniatura) */}
        <img
          className="modal-image"
          src={`${config.API_URL}${data?.Image}`}
          alt={data?.Title}
        />

        <p><strong>A√±o de lanzamiento:</strong> {data?.Release_Year}</p>
        {data?.type === "movies" && <p><strong>Duraci√≥n:</strong> {data?.Duration} min</p>}
        {data?.type === "series" && <p><strong>Temporadas:</strong> {data?.Seasons}</p>}
        <p><strong>Sinopsis:</strong> {data?.Synopsis}</p>

        {/* Secci√≥n de enlaces */}
        <div className="modal-links">
          <h3>Enlaces</h3>
          <ul>
            <li><a href="#">Ver en streaming</a></li>
            <li><a href="#">Tr√°iler oficial</a></li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Modal;
