import React, { useState, useEffect } from "react";
import "../styles/modal.css";
import config from "../utils/config";
import { updateFavorites } from "../utils/favorites"; // Asegúrate de la ruta correcta

const Modal = ({ isOpen, onClose, data, setFavoriteMessage }) => {
  const [isFavorite, setIsFavorite] = useState(false);
  // Estado para la puntuación asignada por el usuario; se inicia en null
  const [userRating, setUserRating] = useState(null);
  // Estado para almacenar la puntuación agregada (promedio)
  const [aggregatedRating, setAggregatedRating] = useState(null);

  // Normaliza el tipo y retorna el identificador según corresponda
  const getFavoriteId = () => {
    if (!data) return null;
    const normalizedType = data.type ? data.type.toLowerCase() : "";
    // Si el tipo es "movie" o "movies", usamos data.Id_Movie; de lo contrario, usamos data.Id_Series
    return normalizedType === "movie" || normalizedType === "movies"
      ? data.Id_Movie
      : data.Id_Series;
  };

  useEffect(() => {
    if (data) {
      const favoriteId = getFavoriteId();
      const userStr = sessionStorage.getItem("user");
      if (userStr) {
        const user = JSON.parse(userStr);
        // Normalizamos Favorites: si no existe, forzamos una cadena vacía
        const favs = (user.Favorites || "")
          .split(",")
          .map(fav => fav.trim())
          .filter(fav => fav !== "");
        setIsFavorite(favs.includes(String(favoriteId)));
      }
      fetchAggregatedRating();
      setUserRating(null);
    }
  }, [data]);

  // Función para obtener el promedio de ratings del contenido
  const fetchAggregatedRating = async () => {
    if (!data) return;
    const itemKey = getFavoriteId();
    try {
      const response = await fetch(
        `${config.API_URL}/api/contenido/${itemKey}/ratings`
      );
      if (response.ok) {
        const ratingData = await response.json();
        setAggregatedRating(ratingData.averageRating);
      } else {
        console.error(
          "❌ Error fetching aggregated rating, status:",
          response.status
        );
      }
    } catch (error) {
      console.error("❌ Error fetching aggregated rating:", error);
    }
  };

  // Función para manejar la selección de rating
  const handleRateSelect = async (value) => {
    setUserRating(value);
    if (!data) return;
    const itemKey = getFavoriteId();
    const userObj = JSON.parse(sessionStorage.getItem("user") || "{}");
    // Normalizamos la ID del usuario para soportar ambos formatos
    const userId = userObj.id || userObj.Id_User;
    const requestBody = {
      userId: userId,
      itemKey: String(itemKey),
      rating: value,
    };

    console.log("📤 Enviando rating al backend:", requestBody);
    try {
      const response = await fetch(`${config.API_URL}/api/usuario/rating`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(requestBody),
      });
      if (!response.ok) {
        throw new Error(
          `Error actualizando rating (Código: ${response.status})`
        );
      }
      const responseData = await response.json();
      console.log("✅ Respuesta del backend (rating):", responseData);
      fetchAggregatedRating();
    } catch (error) {
      console.error("❌ Error actualizando rating desde el modal:", error);
    }
  };

  // Función para manejar favoritos usando la función utilitaria
  const handleFavoriteClick = async () => {
    if (!data) return;
    const favoriteId = getFavoriteId();
    const newFavState = !isFavorite;
    setIsFavorite(newFavState);

    if (typeof setFavoriteMessage === "function") {
      setFavoriteMessage(
        newFavState ? "Añadido a favoritos" : "Eliminado de favoritos"
      );
      setTimeout(() => setFavoriteMessage(""), 2000);
    }
    // Llamamos a la función utilitaria para actualizar favoritos
    await updateFavorites(favoriteId, newFavState);
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="close-button" onClick={onClose}>
          X
        </button>
        <h2>{data?.Title}</h2>

        {/* Botón de Favoritos */}
        <div className="modal-favorite">
          <button onClick={handleFavoriteClick}>
            {isFavorite ? "Eliminar de Favoritos" : "Añadir a Favoritos"}
          </button>
        </div>

        {/* Sección de rating */}
        <div className="modal-rating">
          <h3>Califica este contenido</h3>
          <div className="rating-buttons">
            {Array.from({ length: 10 }, (_, i) => i + 1).map((value) => (
              <button
                key={value}
                onClick={() => handleRateSelect(value)}
                className={`rating-button ${
                  userRating === value ? "selected" : ""
                }`}
              >
                {value}
              </button>
            ))}
          </div>
          <div className="rating-display">
            <span>
              Tu puntuación:{" "}
              {userRating !== null ? userRating : "No seleccionada"}
            </span>
          </div>
          {aggregatedRating != null && !isNaN(aggregatedRating) ? (
            <p className="aggregated-rating">
              Puntuación promedio: {Number(aggregatedRating).toFixed(1)}
            </p>
          ) : (
            <p className="aggregated-rating">Puntuación promedio: 0.0</p>
          )}
        </div>

        <img
          className="modal-image"
          src={`${config.API_URL}${data?.Image}`}
          alt={data?.Title}
        />

        <p>
          <strong>Año de lanzamiento:</strong> {data?.Release_Year}
        </p>
        {data?.type === "movies" && (
          <p>
            <strong>Duración:</strong> {data?.Duration} min
          </p>
        )}
        {data?.type === "series" && (
          <p>
            <strong>Temporadas:</strong> {data?.Seasons}
          </p>
        )}
        <p>
          <strong>Sinopsis:</strong> {data?.Synopsis}
        </p>

        <div className="modal-links">
          <h3>Enlaces</h3>
          <ul>
            <li>
              <a href="#">Ver en streaming</a>
            </li>
            <li>
              <a href="#">Tráiler oficial</a>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Modal;
