import React, { useState, useEffect } from "react";
import config from "../utils/config";
import "../styles/userProfile.css";

const UserProfile = () => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/profile`);
        const data = await response.json();
        setUserData(data);
      } catch (error) {
        console.error("❌ Error al cargar perfil de usuario:", error);
      }
    };

    fetchUserProfile();
  }, []);

  if (!userData) {
    return <p className="loading">Cargando perfil...</p>;
  }

  return (
    <div className="userProfile-container">
      <div className="userProfile-header">
        <img className="avatar" src={`${config.API_URL}/uploads/avatars/${userData.Avatar}`} alt="Avatar" />
        <h2>{userData.Name}</h2>
        <p>{userData.Email}</p>
      </div>

      <div className="userProfile-details">
        <p><strong>Fecha de registro:</strong> {userData.Date_Register}</p>
        <p><strong>Dirección:</strong> {userData.Address}</p>
      </div>

      <div className="userProfile-history">
        <h3>Historial de Visualización</h3>
        <ul>
          {userData.Watching_History?.split(",").map((item, index) => (
            <li key={index}>{item}</li>
          ))}
        </ul>
      </div>

      <div className="userProfile-favorites">
        <h3>Favoritos</h3>
        <ul>
          {userData.Favorites?.split(",").map((item, index) => (
            <li key={index}>{item}</li>
          ))}
        </ul>
      </div>

      <div className="userProfile-subscription">
        <h3>Suscripción</h3>
        <p>{userData.id_subscription ? `Plan ${userData.id_subscription}` : "Sin suscripción"}</p>
      </div>
    </div>
  );
};

export default UserProfile;
