import React from "react";
import config from "../utils/config";
import "../styles/styles.css";

const sampleSeries = [
    { id: 1, title: "Stranger Things", img: `${config.API_URL}/uploads/series/stranger-things.jpg` },
    { id: 2, title: "Breaking Bad", img: `${config.API_URL}/uploads/series/breaking-bad.jpg` },
    { id: 3, title: "The Witcher", img: `${config.API_URL}/uploads/series/the-witcher.jpg` },
    { id: 4, title: "The Mandalorian", img: `${config.API_URL}/uploads/series/the-mandalorian.jpg` },
];

const sampleMovies = [
    { id: 1, title: "Interstellar", img: `${config.API_URL}/uploads/movies/interstellar.jpg` },
    { id: 2, title: "Inception", img: `${config.API_URL}/uploads/movies/inception.jpg` },
    { id: 3, title: "Joker", img: `${config.API_URL}/uploads/movies/joker.jpg` },
    { id: 4, title: "Avengers: Endgame", img: `${config.API_URL}/uploads/movies/endgame.jpg` },
];

const Index = () => {
    return (
        <div className="index-container">
            <h1>Bienvenido a Blockbuster</h1>
            <p>Explora nuestras series y películas.</p>

            <div className="content-section">
                <h2>Series Populares</h2>
                <div className="content-carousel">
                    {sampleSeries.map((series) => (
                        <div key={series.id} className="content-card">
                            <img src={series.img} alt={series.title} />
                            <p>{series.title}</p>
                        </div>
                    ))}
                </div>
            </div>

            <div className="content-section">
                <h2>Películas Destacadas</h2>
                <div className="content-carousel">
                    {sampleMovies.map((movie) => (
                        <div key={movie.id} className="content-card">
                            <img src={movie.img} alt={movie.title} />
                            <p>{movie.title}</p>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Index;
