import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import config from "../utils/config";
import "../styles/styles.css";

const Register = () => {
  const [user, setUser] = useState({
    name: "",
    email: "",
    address: "",
    password: "",
    avatar: "",
  });

  const [errorMessage, setErrorMessage] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage("");

    try {
      const response = await fetch(`${config.API_URL}/register`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(user),
      });

      if (!response.ok) {
        const { message } = await response.json();
        throw new Error(message || "Error al registrar. Inténtalo de nuevo.");
      }

      alert("Registro exitoso.");
      navigate("/login", { replace: true });
    } catch (error) {
      setErrorMessage(error.message);
    }
  };

  return (
    <div className="form-container">
      <div className="form-box">
        <h2>Registro</h2>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <form onSubmit={handleSubmit}>
          <input type="text" name="name" placeholder="Nombre" onChange={handleChange} required />
          <input type="email" name="email" placeholder="Correo" onChange={handleChange} required />
          <input type="text" name="address" placeholder="Dirección" onChange={handleChange} required />
          <div className="password-container">
            <input type={showPassword ? "text" : "password"} name="password" placeholder="Contraseña" onChange={handleChange} required />
            <button type="button" className="toggle-password" onClick={() => setShowPassword(!showPassword)}>
              {showPassword ? "Ocultar" : "Mostrar"}
            </button>
          </div>
          <button type="submit">Registrar</button>
        </form>
        <p>¿Ya tienes cuenta? <a href="/login">Inicia sesión aquí</a></p>
      </div>
    </div>
  );
};

export default Register;
