import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import config from "../utils/config";
import "../styles/userProfile.css";

const UserProfile = () => {
  const { id } = useParams();
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const response = await fetch(`${config.API_URL}/api/usuario/${id}`);
        if (!response.ok) {
          console.error("Respuesta no OK, status:", response.status);
        }
        const data = await response.json();
        setUserData(data);
      } catch (error) {
        console.error("❌ Error al cargar perfil de usuario:", error);
      }
    };

    fetchUserProfile();
  }, [id]);

  // Mapeo para la suscripción, donde las claves son numéricas (1, 2, 3)
  const planMapping = {
    1: "Mensual",
    2: "Trimestral",
    3: "Anual",
  };

  if (!userData) {
    return <p className="loading">Cargando perfil...</p>;
  }

  return (
    <div className="userProfile-container">
      <div className="userProfile-header">
        <img
          className="avatar"
          src={
            userData.Avatar
              ? `${config.API_URL}/uploads/avatars/${userData.Avatar}`
              : `${config.API_URL}/uploads/avatars/default.jpg`
          }
          alt="Avatar"
        />
        <div className="userProfile-info">
          <h2>{userData.Name}</h2>
          <p>{userData.Email}</p>
        </div>
      </div>

      {/* Sección principal con dos columnas: detalles y suscripción */}
      <div className="userProfile-main">
        <div className="userProfile-section userProfile-details">
          <h3>Detalles</h3>
          <p>
            <strong>Fecha de registro:</strong> {userData.Date_Register}
          </p>
          <p>
            <strong>Dirección:</strong> {userData.Address}
          </p>
        </div>

        <div className="userProfile-section userProfile-subscription">
          <h3>Suscripción</h3>
          <p>
            {userData.id_subscription
              ? planMapping[userData.id_subscription] || "Sin suscripción"
              : "Sin suscripción"}
          </p>
        </div>
      </div>

      {/* Sección de Historial y Favoritos, organizados en columnas */}
      <div className="userProfile-grid">
        <div className="userProfile-section userProfile-history">
          <h3>Historial de Visualización</h3>
          <ul>
            {userData.Watching_History?.split(",").map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
        </div>

        <div className="userProfile-section userProfile-favorites">
          <h3>Favoritos</h3>
          <ul>
            {userData.Favorites?.split(",").map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
