// EditProfile.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import config from "../utils/config";
import "../styles/editProfile.css";

const EditProfile = () => {
  const navigate = useNavigate();

  // Estados para datos del usuario, copia original, y modo edición
  const [userData, setUserData] = useState({
    name: "",
    email: "",
    address: "",
    avatar: "",
  });
  const [originalUserData, setOriginalUserData] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  // Carga de datos: consultar el usuario desde el backend usando la ruta GET de edición
  useEffect(() => {
    const loadUser = async () => {
      const storedUser = localStorage.getItem("user");
      if (storedUser) {
        const user = JSON.parse(storedUser);
        const userId = user.id || user.Id_User;
        try {
          const response = await fetch(
            `${config.API_URL}/api/edit-profile/${userId}`
          );
          if (!response.ok) {
            throw new Error("Error al obtener datos desde el servidor");
          }
          const data = await response.json();
          // Normalizamos los campos; Si el avatar está vacío, se asigna el avatar por defecto.
          const fetchedData = {
            name: data.Name || "",
            email: data.Email || "",
            address: data.Address || "",
            avatar:
              data.avatar && data.avatar !== "" && data.avatar !== "undefined"
                ? data.avatar
                : `${config.API_URL}/uploads/avatars/7309681.jpg`,
          };
          setUserData(fetchedData);
          setOriginalUserData(fetchedData);
        } catch (error) {
          setErrorMessage(error.message);
        }
      } else {
        setErrorMessage("No se encontró usuario en almacenamiento.");
      }
    };
    loadUser();
  }, []);

  // Manejo de los cambios en los inputs
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData((prev) => ({ ...prev, [name]: value }));
  };

  // Activa el modo edición sin disparar un submit (preventDefault por seguridad)
  const handleEdit = (e) => {
    e.preventDefault();
    setEditMode(true);
  };

  // Cancela la edición, revierte los cambios y evita el comportamiento por defecto
  const handleCancel = (e) => {
    e.preventDefault();
    setEditMode(false);
    setUserData(originalUserData);
    setErrorMessage("");
    setSuccessMessage("");
  };

  // Enviar la actualización al backend (solo se procesa en modo edición)
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!editMode) return; // Evita que se dispare el submit si no estamos editando

    setLoading(true);
    setErrorMessage("");
    setSuccessMessage("");

    const storedUser = localStorage.getItem("user");
    if (!storedUser) {
      setErrorMessage("No se encontró el usuario en almacenamiento.");
      setLoading(false);
      return;
    }
    const user = JSON.parse(storedUser);
    const userId = user.id || user.Id_User;

    // Objeto con los datos actualizados (ajusta los nombres según tu backend)
    const updatedProfile = {
      Name: userData.name,
      Email: userData.email,
      Address: userData.address,
      avatar: userData.avatar,
    };

    try {
      const response = await fetch(
        `${config.API_URL}/api/edit-profile/${userId}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(updatedProfile),
        }
      );

      if (!response.ok) {
        throw new Error(`Error actualizando perfil (Código: ${response.status})`);
      }

      const updatedUserData = await response.json();
      // Normalizamos el usuario, asegurándonos de que la identificación esté en "id"
      const normalizedUser = {
        id: updatedUserData.id || updatedUserData.Id_User,
        ...updatedUserData,
      };

      // Actualiza la copia original y guarda en localStorage
      const newData = {
        name: normalizedUser.Name || "",
        email: normalizedUser.Email || "",
        address: normalizedUser.Address || "",
        avatar: normalizedUser.avatar || `${config.API_URL}/uploads/avatars/7309681.jpg`,
      };
      setOriginalUserData(newData);
      setUserData(newData);
      localStorage.setItem("user", JSON.stringify(normalizedUser));
      setSuccessMessage("Perfil actualizado correctamente.");
      setEditMode(false);
      setLoading(false);

      setTimeout(() => {
        navigate(`/usuario/${normalizedUser.id}`);
      }, 1500);
    } catch (error) {
      console.error("Error actualizando el perfil:", error);
      setErrorMessage(error.message);
      setLoading(false);
    }
  };

  return (
    <div className="edit-profile-container">
      <h2>Editar Perfil</h2>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      {successMessage && <p className="success-message">{successMessage}</p>}
      <form onSubmit={handleSubmit} className="edit-profile-form">
        <label>
          Nombre:
          <input
            type="text"
            name="name"
            value={userData.name}
            onChange={handleChange}
            disabled={!editMode}
            required
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={userData.email}
            onChange={handleChange}
            disabled={!editMode}
            required
          />
        </label>
        <label>
          Dirección:
          <input
            type="text"
            name="address"
            value={userData.address}
            onChange={handleChange}
            disabled={!editMode}
          />
        </label>
        <label>
          Avatar URL:
          <input
            type="text"
            name="avatar"
            value={userData.avatar}
            onChange={handleChange}
            disabled={!editMode}
          />
        </label>
        <div className="avatar-preview">
          <img
            src={userData.avatar ? userData.avatar : `${config.API_URL}/uploads/avatars/7309681.jpg`}
            alt="Vista previa del avatar"
          />
        </div>
        {!editMode ? (
          <div className="action-buttons">
            <button type="button" onClick={handleEdit}>
              Editar
            </button>
          </div>
        ) : (
          <div className="action-buttons">
            <button type="submit" disabled={loading}>
              {loading ? "Guardando..." : "Guardar Cambios"}
            </button>
            <button type="button" onClick={handleCancel}>
              Cancelar
            </button>
          </div>
        )}
      </form>
    </div>
  );
};

export default EditProfile;
