import React, { useState } from "react";
import config from "../utils/config";
import "../styles/styles.css";
import Modal from "../components/Modal"; // Importamos el nuevo modal

const Series = () => {
  const [selectedSeries, setSelectedSeries] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleClick = async (seriesId) => {
    try {
      const response = await fetch(`${config.API_URL}/api/series/${seriesId}`);
      const data = await response.json();
      setSelectedSeries(data);
      setIsModalOpen(true);
    } catch (error) {
      console.error("Error al cargar la serie:", error);
    }
  };
  

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedSeries(null);
  };

  return (
    <div className="content-page">
      <h1>Series</h1>
      
      <div className="content-carousel">
        {seriesList.map(series => (
          <div key={series.id} className="content-card" onClick={() => handleClick(series.id)}>
            <img src={series.img} alt={series.title} />
            <p>{series.title}</p>
          </div>
        ))}
      </div>

      <Modal isOpen={isModalOpen} onClose={closeModal} data={selectedSeries} />
    </div>
  );
};

export default Series;
