import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import config from "../utils/config";
import "../styles/styles.css";

const Register = () => {
  const [user, setUser] = useState({
    name: "",
    email: "",
    address: "",
    password: "",
    avatar: ""
  });

  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage("");

    try {
      const response = await fetch(`${config.API_URL}/register`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(user),
      });

      if (!response.ok) {
        const { message } = await response.json();
        throw new Error(message || "Error al registrar. IntÃ©ntalo de nuevo.");
      }

      alert("Registro exitoso.");
      navigate("/login", { replace: true }); // âœ… Ahora redirige al login tras el registro
    } catch (error) {
      setErrorMessage(error.message);
    }
  };

  return (
    <div>
      <h2>Registro</h2>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Nombre" onChange={handleChange} required />
        <input type="email" name="email" placeholder="Correo" onChange={handleChange} required />
        <input type="text" name="address" placeholder="DirecciÃ³n" onChange={handleChange} required />
        <input type="password" name="password" placeholder="ContraseÃ±a" onChange={handleChange} required />
        <button type="submit">Registrar</button>
      </form>
      <p>Â¿Ya tienes cuenta? <a href="/login">Inicia sesiÃ³n aquÃ­</a></p> {/* ðŸ”¹ Nueva opciÃ³n para usuarios registrados */}
    </div>
  );
};

export default Register;
