import React, { useEffect, useState } from "react";
import config from "../utils/config";
import "../styles/styles.css";

const AdminUsers = () => {
    const [users, setUsers] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [usersPerPage, setUsersPerPage] = useState(10);
    const [searchQuery, setSearchQuery] = useState("");
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchUsers = async () => {
            const accessToken = sessionStorage.getItem("accessToken");

            if (!accessToken) {
                console.error("❌ No hay token en sessionStorage. ¿El usuario está autenticado?");
                setError("❌ No hay token de autenticación. Inicia sesión nuevamente.");
                return;
            }
            
            console.log("📌 Token enviado en la solicitud:", accessToken);

            try {
                const response = await fetch(`${config.API_URL}/api/admin/users`, {
                    method: "GET",
                    headers: { 
                        "Authorization": `Bearer ${accessToken}`,
                        "Content-Type": "application/json"
                    }
                });

                console.log("📌 Respuesta cruda del servidor:", response);

                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.message || "Error al obtener usuarios.");
                }

                const data = await response.json();
                setUsers(data);
            } catch (error) {
                console.error("❌ Error en fetchUsers:", error);
                setError(error.message);
            }
        };

        fetchUsers();
    }, []);

    const handleDelete = async (userId) => {
        const accessToken = sessionStorage.getItem("accessToken");

        if (!accessToken) {
            console.error("❌ No se encontró un token en sessionStorage.");
            setError("❌ No hay token de autenticación.");
            return;
        }

        try {
            const response = await fetch(`${config.API_URL}/api/admin/users/${userId}`, {
                method: "DELETE",
                headers: { 
                    "Authorization": `Bearer ${accessToken}`,
                    "Content-Type": "application/json"
                }
            });

            console.log("📌 Respuesta DELETE del servidor:", response);

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || "Error al eliminar usuario.");
            }

            setUsers(users.filter(user => user.id !== userId));
        } catch (error) {
            console.error("Error en handleDelete:", error);
            setError(error.message);
        }
    };

    const handlePageChange = (newPage) => setCurrentPage(newPage);
    const handleUsersPerPageChange = (e) => setUsersPerPage(Number(e.target.value));
    const handleSearchChange = (e) => setSearchQuery(e.target.value.toLowerCase());

    const filteredUsers = users.filter(user =>
        user.name.toLowerCase().includes(searchQuery) || user.email.toLowerCase().includes(searchQuery)
    );

    const indexOfLastUser = currentPage * usersPerPage;
    const indexOfFirstUser = indexOfLastUser - usersPerPage;
    const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);

    return (
        <div className="admin-container">
            <h1>Gestionar Usuarios</h1>
            {error && <p className="error-message">{error}</p>}

            {/* Buscador */}
            <input 
                type="text" 
                placeholder="Buscar usuario..." 
                onChange={handleSearchChange} 
                className="search-box"
            />

            {/* Paginación */}
            <select onChange={handleUsersPerPageChange}>
                <option value="5">5 por página</option>
                <option value="10">10 por página</option>
                <option value="25">25 por página</option>
            </select>

            <table className="users-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nombre</th>
                        <th>Correo</th>
                        <th>Rol</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {currentUsers.map(user => (
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>{user.role}</td>
                            <td>
                                <button className="delete-btn" onClick={() => handleDelete(user.id)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Botones de paginación */}
            <div className="pagination">
                {Array.from({ length: Math.ceil(filteredUsers.length / usersPerPage) }, (_, index) => (
                    <button key={index} onClick={() => handlePageChange(index + 1)}>
                        {index + 1}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default AdminUsers;
