import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import config from "../utils/config";
import "../styles/editProfile.css"; // Crea este archivo para los estilos que desees

const EditProfile = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState({
    name: "",
    email: "",
    address: "",
    avatar: "",
  });
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  // Al montar el componente, lee el usuario desde localStorage y normaliza sus campos
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUserData({
          name: parsedUser.Name || "", // O ajusta según cómo se llame el campo en tu backend (por ejemplo, parsedUser.name)
          email: parsedUser.Email || "",
          address: parsedUser.Address || "",
          avatar: parsedUser.avatar || parsedUser.Avatar || "",
        });
      } catch (err) {
        console.error("Error al parsear el usuario almacenado:", err);
      }
    }
  }, []);

  // Actualiza el estado del formulario conforme el usuario modifica cada campo
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData((prev) => ({ ...prev, [name]: value }));
  };

  // Al enviar el formulario, actualizamos el perfil del usuario
  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage("");
    setSuccessMessage("");

    const storedUser = localStorage.getItem("user");
    if (!storedUser) {
      setErrorMessage("No se encontró el usuario en almacenamiento.");
      return;
    }

    const user = JSON.parse(storedUser);
    const userId = user.id || user.Id_User; // Normalizamos el identificador

    // Construimos el objeto con los campos actualizados
    const updatedProfile = {
      Name: userData.name,
      Email: userData.email,
      Address: userData.address,
      avatar: userData.avatar,
    };

    try {
      const response = await fetch(
        `${config.API_URL}/api/usuario/${userId}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(updatedProfile),
        }
      );

      if (!response.ok) {
        throw new Error(`Error actualizando perfil (Código: ${response.status})`);
      }

      const updatedUserData = await response.json();

      // Normalizamos el objeto usuario para asegurarnos de que tenga siempre la propiedad "id"
      const normalizedUser = {
        id: updatedUserData.id || updatedUserData.Id_User,
        ...updatedUserData,
      };

      localStorage.setItem("user", JSON.stringify(normalizedUser));
      setSuccessMessage("Perfil actualizado correctamente.");

      // Opcional: Después de unos segundos, redirigimos al perfil del usuario
      setTimeout(() => {
        navigate(`/usuario/${normalizedUser.id}`);
      }, 1500);
    } catch (error) {
      console.error("Error actualizando el perfil:", error);
      setErrorMessage(error.message);
    }
  };

  return (
    <div className="edit-profile-container">
      <h2>Editar Perfil</h2>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      {successMessage && <p className="success-message">{successMessage}</p>}
      <form onSubmit={handleSubmit} className="edit-profile-form">
        <label>
          Nombre:
          <input
            type="text"
            name="name"
            value={userData.name}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={userData.email}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Dirección:
          <input
            type="text"
            name="address"
            value={userData.address}
            onChange={handleChange}
          />
        </label>
        <label>
          Avatar URL:
          <input
            type="text"
            name="avatar"
            value={userData.avatar}
            onChange={handleChange}
          />
        </label>
        {userData.avatar && (
          <div className="avatar-preview">
            <img src={userData.avatar} alt="Vista previa del avatar" />
          </div>
        )}
        <button type="submit">Guardar Cambios</button>
      </form>
    </div>
  );
};

export default EditProfile;
